import React, { useState, useRef, useEffect } from 'react';
import './Header.css';

const Header = ({ user, onLogout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuRef = useRef(null);

  // Cerrar menú cuando se hace clic fuera
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLogout = () => {
    setIsMenuOpen(false);
    if (onLogout) {
      onLogout();
    }
  };

  return (
    <header className="header">
      <div className="header-left">
        <div className="gym-logo">
          <img src="/cloudfit_l.png" alt="Gym Logo" className="logo-image" />
          <h1 className="gym-name"> CloudFit </h1>
        </div>
      </div>
      
      <div className="header-right">
        <div className="user-info" onClick={toggleMenu} ref={menuRef}>
          <span className="username">{user?.username || 'Usuario'}</span>
          <img 
            src="/usuario_logo.png" 
            alt="Usuario" 
            className="user-avatar"
          />
          
          {/* Menú desplegable */}
          {isMenuOpen && (
            <div className="user-menu">
              <div className="user-menu-header">
                <img 
                  src="/usuario_logo.png" 
                  alt="Usuario" 
                  className="menu-avatar"
                />
                <div className="user-details">
                  <span className="menu-username">{user?.username || 'Usuario'}</span>
                  <span className="menu-email">{user?.email || 'usuario@cloudfit.com'}</span>
                </div>
              </div>
              
              <div className="menu-divider"></div>
              
              <div className="menu-items">
                <button className="menu-item">
                  <span className="menu-icon">👤</span>
                  <span>Mi Perfil</span>
                </button>
                
                <button className="menu-item">
                  <span className="menu-icon">⚙️</span>
                  <span>Configuración</span>
                </button>
                
                <button className="menu-item">
                  <span className="menu-icon">📊</span>
                  <span>Mi Progreso</span>
                </button>
                
                <div className="menu-divider"></div>
                
                <button className="menu-item logout-item" onClick={handleLogout}>
                  <span className="menu-icon">🚪</span>
                  <span>Cerrar Sesión</span>
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
