import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import UsersManagement from './components/gdu.jsx';
import EmployeesManagement from './components/gde.jsx';
import MachinesManagement from './components/gdm.jsx';
import './AdminMenu.css';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState('dashboard');
  const [stats, setStats] = useState({
    totalUsers: 0,
    activeSubscriptions: 0,
    totalRevenue: 0,
    totalEmployees: 0
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    setLoading(true);
    try {
      // Aqu√≠ har√≠as las llamadas a tu API
      // const statsResponse = await fetch('/api/admin/stats');
      
      // Datos de ejemplo
      setStats({
        totalUsers: 156,
        activeSubscriptions: 142,
        totalRevenue: 45280.50,
        totalEmployees: 12
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Funci√≥n para cerrar sesi√≥n (siguiendo el patr√≥n de login/registro)
  const handleLogout = () => {
    // Confirmar antes de cerrar sesi√≥n
    if (window.confirm('¬øEst√°s seguro de que quieres cerrar sesi√≥n?')) {
      try {
        // Limpiar localStorage (igual que hacen login/registro)
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        
        // Verificar que se limpiaron los datos
        console.log('Sesi√≥n cerrada correctamente');
        
        // Redireccionar al login
        navigate('/');
      } catch (error) {
        console.error('Error al cerrar sesi√≥n:', error);
        // A√∫n as√≠ redirigir al login
        navigate('/');
      }
    }
  };

  const menuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: 'üìä' },
    { id: 'users', label: 'Gesti√≥n de Usuarios', icon: 'üë•' },
    { id: 'employees', label: 'Gesti√≥n de Empleados', icon: 'üë®‚Äçüíº' },
    { id: 'machines', label: 'Gesti√≥n de M√°quinas', icon: 'üèãÔ∏è‚Äç‚ôÇÔ∏è' },
    { id: 'settings', label: 'Planes de Pago', icon: '‚öôÔ∏è' }
  ];

  const renderDashboard = () => (
    <div className="dashboard-content">
      <h2>Panel de Control - CloudFit Admin</h2>
      
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üë•</div>
          <div className="stat-info">
            <h3>{stats.totalUsers}</h3>
            <p>Usuarios Registrados</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚úÖ</div>
          <div className="stat-info">
            <h3>{stats.activeSubscriptions}</h3>
            <p>Suscripciones Activas</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üí∞</div>
          <div className="stat-info">
            <h3>${stats.totalRevenue.toLocaleString()}</h3>
            <p>Ingresos Totales</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üë®‚Äçüíº</div>
          <div className="stat-info">
            <h3>{stats.totalEmployees}</h3>
            <p>Empleados Activos</p>
          </div>
        </div>
      </div>
    </div>
  );

  const renderPaymentPlans = () => (
    <div className="settings-content">
      <h2>Configuraci√≥n de Planes de Pago</h2>
      <div className="plans-management">
        <div className="current-plans">
          <h3>Planes Actuales</h3>
          <div className="plans-grid">
            <div className="plan-config-card">
              <h4>Plan Mensual</h4>
              <div className="plan-price">$29.99/mes</div>
              <ul className="plan-features-list">
                <li>Acceso completo a la plataforma</li>
                <li>Soporte t√©cnico 24/7</li>
                <li>Actualizaciones incluidas</li>
              </ul>
              <button className="btn-secondary">Editar Plan</button>
            </div>
            
            <div className="plan-config-card">
              <h4>Plan Anual</h4>
              <div className="plan-price">$299.99/a√±o</div>
              <div className="plan-discount">¬°Ahorra 2 meses!</div>
              <ul className="plan-features-list">
                <li>Acceso completo a la plataforma</li>
                <li>Soporte t√©cnico prioritario</li>
                <li>Actualizaciones incluidas</li>
                <li>Funciones premium exclusivas</li>
              </ul>
              <button className="btn-secondary">Editar Plan</button>
            </div>
          </div>
        </div>

      
      </div>
    </div>
  );

  const renderContent = () => {
    switch (activeSection) {
      case 'dashboard':
        return renderDashboard();
      case 'users':
        return <UsersManagement />;
      case 'employees':
        return <EmployeesManagement />;
      case 'machines':
        return <MachinesManagement />;
      case 'settings':
        return renderPaymentPlans();
      default:
        return renderDashboard();
    }
  };

  return (
    <div className="admin-dashboard">
      {/* Header */}
      <header className="admin-header">
        <div className="header-left">
          <img src="cloudfit_l.png" alt="CloudFit" className="logo" />
          <h1>CloudFit Admin</h1>
        </div>
        <div className="header-right">
          <div className="admin-profile">
            <span>üë®‚Äçüíº Admin</span>
            <button className="logout-btn" onClick={handleLogout}>
              Cerrar Sesi√≥n
            </button>
          </div>
        </div>
      </header>

      <div className="admin-layout">
        {/* Sidebar */}
        <aside className="admin-sidebar">
          <nav className="admin-nav">
            {menuItems.map(item => (
              <button
                key={item.id}
                className={`nav-item ${activeSection === item.id ? 'active' : ''}`}
                onClick={() => setActiveSection(item.id)}
              >
                <span className="nav-icon">{item.icon}</span>
                <span className="nav-label">{item.label}</span>
              </button>
            ))}
          </nav>
        </aside>

        {/* Main Content */}
        <main className="admin-main">
          {loading ? (
            <div className="loading">
              <div className="spinner"></div>
              <p>Cargando...</p>
            </div>
          ) : (
            renderContent()
          )}
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
