import React, { useState, useEffect } from 'react';

const MachinesManagement = () => {
  const [machines, setMachines] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [selectedMachine, setSelectedMachine] = useState(null);
  const [filterCategory, setFilterCategory] = useState('todas');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);

  // Estado del formulario
  const [formData, setFormData] = useState({
    nombre: '',
    categoria: '',
    pesos: '',
    nombreVideo: '',
    nombreImagen: '',
    video: null,
    imagen: null
  });

  const [formErrors, setFormErrors] = useState({});

  const categories = [
    { value: 'cardio', label: 'Cardio' },
    { value: 'fuerza', label: 'Fuerza' },
    { value: 'funcional', label: 'Funcional' },
    { value: 'libre', label: 'Libre' }
  ];

  // Cargar máquinas al montar el componente
  useEffect(() => {
    loadMachines();
  }, []);

  const loadMachines = async () => {
    try {
      setLoading(true);
      // Aquí harías la llamada a tu API
      const response = await fetch('/api/machines');
      const data = await response.json();
      setMachines(data);
    } catch (error) {
      console.error('Error al cargar máquinas:', error);
    } finally {
      setLoading(false);
    }
  };

  const openAddModal = () => {
    setFormData({
      nombre: '',
      categoria: '',
      pesos: '',
      nombreVideo: '',
      nombreImagen: '',
      video: null,
      imagen: null
    });
    setFormErrors({});
    setShowAddModal(true);
  };

  const closeModal = () => {
    setShowAddModal(false);
    setShowDetailsModal(false);
    setSelectedMachine(null);
    setFormData({
      nombre: '',
      categoria: '',
      pesos: '',
      nombreVideo: '',
      nombreImagen: '',
      video: null,
      imagen: null
    });
    setFormErrors({});
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Limpiar error del campo cuando el usuario empiece a escribir
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleFileChange = (e, fileType) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({
        ...prev,
        [fileType]: file
      }));
      
      // Limpiar error del archivo
      if (formErrors[fileType]) {
        setFormErrors(prev => ({
          ...prev,
          [fileType]: ''
        }));
      }
    }
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.nombre.trim()) {
      errors.nombre = 'El nombre de la máquina es requerido';
    }

    if (!formData.categoria) {
      errors.categoria = 'La categoría es requerida';
    }

    if (!formData.pesos.trim()) {
      errors.pesos = 'Los pesos disponibles son requeridos';
    }

    if (!formData.nombreVideo.trim()) {
      errors.nombreVideo = 'El nombre del video es requerido';
    }

    if (!formData.nombreImagen.trim()) {
      errors.nombreImagen = 'El nombre de la imagen es requerido';
    }

    if (!formData.video) {
      errors.video = 'El video es requerido';
    } else if (!formData.video.type.startsWith('video/')) {
      errors.video = 'El archivo debe ser un video';
    }

    if (!formData.imagen) {
      errors.imagen = 'La imagen es requerida';
    } else if (!formData.imagen.type.startsWith('image/')) {
      errors.imagen = 'El archivo debe ser una imagen';
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const saveFilesToPublic = async (videoFile, imageFile, videoName, imageName) => {
    try {
      // Crear FormData para enviar los archivos
      const formData = new FormData();
      formData.append('video', videoFile);
      formData.append('imagen', imageFile);
      formData.append('nombreVideo', videoName);
      formData.append('nombreImagen', imageName);

      const response = await fetch('/api/upload-files', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Error al subir archivos');
      }

      return await response.json();
    } catch (error) {
      console.error('Error al guardar archivos:', error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    try {
      setLoading(true);

      // Primero guardar los archivos en public
      await saveFilesToPublic(
        formData.video,
        formData.imagen,
        formData.nombreVideo,
        formData.nombreImagen
      );

      // Luego guardar los datos en MongoDB
      const machineData = {
        nombre: formData.nombre,
        categoria: formData.categoria,
        pesos: formData.pesos,
        nombreVideo: formData.nombreVideo,
        nombreImagen: formData.nombreImagen
      };

      const response = await fetch('/api/machines', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(machineData)
      });

      if (!response.ok) {
        throw new Error('Error al guardar la máquina');
      }

      const newMachine = await response.json();
      setMachines(prev => [...prev, newMachine]);
      closeModal();
      
      // Mostrar mensaje de éxito (puedes usar una librería de notificaciones)
      alert('Máquina registrada exitosamente');

    } catch (error) {
      console.error('Error al guardar máquina:', error);
      alert('Error al guardar la máquina: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteMachine = async (machineId) => {
    if (!confirm('¿Estás seguro de que quieres eliminar esta máquina?')) {
      return;
    }

    try {
      setLoading(true);
      const response = await fetch(`/api/machines/${machineId}`, {
        method: 'DELETE'
      });

      if (!response.ok) {
        throw new Error('Error al eliminar la máquina');
      }

      setMachines(prev => prev.filter(machine => machine._id !== machineId));
      alert('Máquina eliminada exitosamente');
    } catch (error) {
      console.error('Error al eliminar máquina:', error);
      alert('Error al eliminar la máquina');
    } finally {
      setLoading(false);
    }
  };

  const openDetailsModal = (machine) => {
    setSelectedMachine(machine);
    setShowDetailsModal(true);
  };

  // Filtrar máquinas
  const filteredMachines = machines.filter(machine => {
    const matchesCategory = filterCategory === 'todas' || machine.categoria === filterCategory;
    const matchesSearch = machine.nombre.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  // Estadísticas
  const stats = {
    total: machines.length,
    cardio: machines.filter(m => m.categoria === 'cardio').length,
    fuerza: machines.filter(m => m.categoria === 'fuerza').length,
    funcional: machines.filter(m => m.categoria === 'funcional').length,
    libre: machines.filter(m => m.categoria === 'libre').length
  };

  return (
    <div className="machines-management">
      <div className="section-header">
        <h2>Gestión de Máquinas</h2>
        <button className="btn-primary" onClick={openAddModal} disabled={loading}>
          + Nueva Máquina
        </button>
      </div>

      {/* Estadísticas */}
      <div className="stats-grid">
        <div className="stat-card">
          <h4>{stats.total}</h4>
          <p>Total Máquinas</p>
        </div>
        <div className="stat-card">
          <h4>{stats.cardio}</h4>
          <p>Cardio</p>
        </div>
        <div className="stat-card">
          <h4>{stats.fuerza}</h4>
          <p>Fuerza</p>
        </div>
        <div className="stat-card">
          <h4>{stats.funcional}</h4>
          <p>Funcional</p>
        </div>
        <div className="stat-card">
          <h4>{stats.libre}</h4>
          <p>Libre</p>
        </div>
      </div>

      {/* Filtros */}
      <div className="filters-section">
        <div className="filter-group">
          <label>Buscar:</label>
          <input
            type="text"
            placeholder="Buscar por nombre..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>
        
        <div className="filter-group">
          <label>Categoría:</label>
          <select
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
            className="filter-select"
          >
            <option value="todas">Todas las categorías</option>
            {categories.map(cat => (
              <option key={cat.value} value={cat.value}>{cat.label}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Lista de máquinas */}
      <div className="machines-grid">
        {loading && <div className="loading">Cargando...</div>}
        
        {!loading && filteredMachines.length === 0 && (
          <div className="no-machines">
            <p>No se encontraron máquinas</p>
          </div>
        )}

        {!loading && filteredMachines.map(machine => (
          <div key={machine._id} className="machine-card">
            <div className="machine-image">
              <img
                src={`/public/${machine.nombreImagen}`}
                alt={machine.nombre}
                onError={(e) => {
                  e.target.src = '/placeholder-machine.jpg';
                }}
              />
            </div>
            
            <div className="machine-info">
              <h3>{machine.nombre}</h3>
              <p className="machine-category">{machine.categoria}</p>
              <p className="machine-weights">Pesos: {machine.pesos}</p>
            </div>
            
            <div className="machine-actions">
              <button
                className="btn-secondary"
                onClick={() => openDetailsModal(machine)}
              >
                Ver Detalles
              </button>
              <button
                className="btn-danger"
                onClick={() => deleteMachine(machine._id)}
                disabled={loading}
              >
                Eliminar
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal para agregar máquina */}
      {showAddModal && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content large-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Nueva Máquina</h3>
              <button className="close-btn" onClick={closeModal}>✕</button>
            </div>
            
            <form onSubmit={handleSubmit} className="modal-body">
              <div className="form-grid">
                <div className="form-group">
                  <label>Nombre de la Máquina *</label>
                  <input
                    type="text"
                    name="nombre"
                    value={formData.nombre}
                    onChange={handleInputChange}
                    className={formErrors.nombre ? 'error' : ''}
                    placeholder="Ej: Caminadora ProFit 3000"
                  />
                  {formErrors.nombre && <span className="error-text">{formErrors.nombre}</span>}
                </div>

                <div className="form-group">
                  <label>Categoría *</label>
                  <select
                    name="categoria"
                    value={formData.categoria}
                    onChange={handleInputChange}
                    className={formErrors.categoria ? 'error' : ''}
                  >
                    <option value="">Seleccionar categoría</option>
                    {categories.map(cat => (
                      <option key={cat.value} value={cat.value}>{cat.label}</option>
                    ))}
                  </select>
                  {formErrors.categoria && <span className="error-text">{formErrors.categoria}</span>}
                </div>

                <div className="form-group full-width">
                  <label>Pesos Disponibles *</label>
                  <input
                    type="text"
                    name="pesos"
                    value={formData.pesos}
                    onChange={handleInputChange}
                    className={formErrors.pesos ? 'error' : ''}
                    placeholder="Ej: 10kg, 15kg, 20kg, 25kg"
                  />
                  {formErrors.pesos && <span className="error-text">{formErrors.pesos}</span>}
                </div>

                <div className="form-group">
                  <label>Nombre del Video *</label>
                  <input
                    type="text"
                    name="nombreVideo"
                    value={formData.nombreVideo}
                    onChange={handleInputChange}
                    className={formErrors.nombreVideo ? 'error' : ''}
                    placeholder="Ej: tutorial_caminadora.mp4"
                  />
                  {formErrors.nombreVideo && <span className="error-text">{formErrors.nombreVideo}</span>}
                </div>

                <div className="form-group">
                  <label>Nombre de la Imagen *</label>
                  <input
                    type="text"
                    name="nombreImagen"
                    value={formData.nombreImagen}
                    onChange={handleInputChange}
                    className={formErrors.nombreImagen ? 'error' : ''}
                    placeholder="Ej: caminadora_principal.jpg"
                  />
                  {formErrors.nombreImagen && <span className="error-text">{formErrors.nombreImagen}</span>}
                </div>

                <div className="form-group">
                  <label>Video Tutorial *</label>
                  <input
                    type="file"
                    accept="video/*"
                    onChange={(e) => handleFileChange(e, 'video')}
                    className={formErrors.video ? 'error' : ''}
                  />
                  {formData.video && <p className="file-selected">Archivo: {formData.video.name}</p>}
                  {formErrors.video && <span className="error-text">{formErrors.video}</span>}
                </div>

                <div className="form-group">
                  <label>Imagen de la Máquina *</label>
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => handleFileChange(e, 'imagen')}
                    className={formErrors.imagen ? 'error' : ''}
                  />
                  {formData.imagen && <p className="file-selected">Archivo: {formData.imagen.name}</p>}
                  {formErrors.imagen && <span className="error-text">{formErrors.imagen}</span>}
                </div>
              </div>
            </form>
            
            <div className="modal-footer">
              <button type="button" className="btn-secondary" onClick={closeModal}>
                Cancelar
              </button>
              <button
                type="submit"
                className="btn-primary"
                onClick={handleSubmit}
                disabled={loading}
              >
                {loading ? 'Guardando...' : 'Guardar Máquina'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal de detalles */}
      {showDetailsModal && selectedMachine && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content large-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>{selectedMachine.nombre}</h3>
              <button className="close-btn" onClick={closeModal}>✕</button>
            </div>
            
            <div className="modal-body">
              <div className="machine-details">
                <div className="detail-section">
                  <img
                    src={`/public/${selectedMachine.nombreImagen}`}
                    alt={selectedMachine.nombre}
                    className="machine-detail-image"
                    onError={(e) => {
                      e.target.src = '/placeholder-machine.jpg';
                    }}
                  />
                </div>
                
                <div className="detail-section">
                  <h4>Información General</h4>
                  <p><strong>Categoría:</strong> {selectedMachine.categoria}</p>
                  <p><strong>Pesos Disponibles:</strong> {selectedMachine.pesos}</p>
                  
                  <h4>Archivos</h4>
                  <p><strong>Video:</strong> {selectedMachine.nombreVideo}</p>
                  <p><strong>Imagen:</strong> {selectedMachine.nombreImagen}</p>
                  
                  <div className="video-section">
                    <h4>Video Tutorial</h4>
                    <video
                      controls
                      width="100%"
                      style={{ maxWidth: '400px' }}
                    >
                      <source src={`/public/${selectedMachine.nombreVideo}`} type="video/mp4" />
                      Tu navegador no soporta el elemento video.
                    </video>
                  </div>
                </div>
              </div>
            </div>
            
            <div className="modal-footer">
              <button className="btn-secondary" onClick={closeModal}>Cerrar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MachinesManagement;
