import React, { useState } from 'react';
import { ChevronLeft, ChevronRight, User, CreditCard, Check, Calendar, Shield } from 'lucide-react';

const CloudFitRegistration = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [errors, setErrors] = useState({});
  
  const [formData, setFormData] = useState({
    // Paso 1: Datos iniciales
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    
    // Paso 2: Plan de suscripción
    planType: '',
    
    // Paso 3: Datos de tarjeta
    cardNumber: '',
    cardName: '',
    expiryDate: '',
    cvv: '',
    billingAddress: '',
    city: '',
    zipCode: ''
  });

  const plans = [
    {
      id: 'monthly',
      name: 'Plan Mensual',
      price: '$29.99',
      period: '/mes',
      features: ['Acceso completo al gimnasio', 'Clases grupales', 'App móvil', 'Soporte 24/7'],
      popular: false
    },
    {
      id: 'annual',
      name: 'Plan Anual',
      price: '$299.99',
      period: '/año',
      originalPrice: '$359.88',
      features: ['Acceso completo al gimnasio', 'Clases grupales', 'App móvil', 'Soporte 24/7', 'Entrenador personal', '2 meses GRATIS'],
      popular: true,
      discount: '17% OFF'
    }
  ];

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const validateStep = (step) => {
    const newErrors = {};
    
    if (step === 1) {
      if (!formData.firstName.trim()) newErrors.firstName = 'Nombre requerido';
      if (!formData.lastName.trim()) newErrors.lastName = 'Apellido requerido';
      if (!formData.email.trim()) newErrors.email = 'Email requerido';
      else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email inválido';
      if (!formData.password) newErrors.password = 'Contraseña requerida';
      else if (formData.password.length < 6) newErrors.password = 'Mínimo 6 caracteres';
      if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Las contraseñas no coinciden';
      if (!formData.phone.trim()) newErrors.phone = 'Teléfono requerido';
    }
    
    if (step === 2) {
      if (!formData.planType) newErrors.planType = 'Selecciona un plan';
    }
    
    if (step === 3) {
      if (!formData.cardNumber.trim()) newErrors.cardNumber = 'Número de tarjeta requerido';
      else if (formData.cardNumber.replace(/\s/g, '').length !== 16) newErrors.cardNumber = 'Número de tarjeta inválido';
      if (!formData.cardName.trim()) newErrors.cardName = 'Nombre del titular requerido';
      if (!formData.expiryDate.trim()) newErrors.expiryDate = 'Fecha de vencimiento requerida';
      if (!formData.cvv.trim()) newErrors.cvv = 'CVV requerido';
      else if (formData.cvv.length !== 3) newErrors.cvv = 'CVV debe tener 3 dígitos';
      if (!formData.billingAddress.trim()) newErrors.billingAddress = 'Dirección requerida';
      if (!formData.city.trim()) newErrors.city = 'Ciudad requerida';
      if (!formData.zipCode.trim()) newErrors.zipCode = 'Código postal requerido';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const nextStep = () => {
    if (validateStep(currentStep)) {
      setCurrentStep(prev => Math.min(prev + 1, 4));
    }
  };

  const prevStep = () => {
    setCurrentStep(prev => Math.max(prev - 1, 1));
  };

  const formatCardNumber = (value) => {
    const v = value.replace(/\s+/g, '').replace(/[^0-9]/gi, '');
    const matches = v.match(/\d{4,16}/g);
    const match = matches && matches[0] || '';
    const parts = [];
    for (let i = 0, len = match.length; i < len; i += 4) {
      parts.push(match.substring(i, i + 4));
    }
    if (parts.length) {
      return parts.join(' ');
    } else {
      return v;
    }
  };

  const handleSubmit = async () => {
    if (!validateStep(3)) return;
    
    setIsLoading(true);
    // Simular proceso de registro
    await new Promise(resolve => setTimeout(resolve, 2000));
    setIsLoading(false);
    setCurrentStep(4);
  };

  const renderProgressBar = () => (
    <div className="mb-8">
      <div className="flex items-center justify-between mb-4">
        {[1, 2, 3].map((step) => (
          <div key={step} className="flex items-center">
            <div className={`w-10 h-10 rounded-full flex items-center justify-center text-sm font-semibold transition-all duration-300 ${
              currentStep >= step 
                ? 'bg-gradient-to-r from-green-400 to-green-500 text-white shadow-lg' 
                : 'bg-gray-200 text-gray-500'
            }`}>
              {currentStep > step ? <Check size={16} /> : step}
            </div>
            {step < 3 && (
              <div className={`h-1 w-20 mx-2 transition-all duration-300 ${
                currentStep > step ? 'bg-green-400' : 'bg-gray-200'
              }`} />
            )}
          </div>
        ))}
      </div>
      <div className="text-center text-sm text-gray-600">
        {currentStep === 1 && 'Información Personal'}
        {currentStep === 2 && 'Seleccionar Plan'}
        {currentStep === 3 && 'Información de Pago'}
        {currentStep === 4 && 'Registro Completado'}
      </div>
    </div>
  );

  const renderStep1 = () => (
    <div className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <input
            type="text"
            placeholder="Nombre"
            value={formData.firstName}
            onChange={(e) => handleInputChange('firstName', e.target.value)}
            className={`w-full p-3 border-2 rounded-lg transition-all ${errors.firstName ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
          />
          {errors.firstName && <p className="text-red-500 text-xs mt-1">{errors.firstName}</p>}
        </div>
        <div>
          <input
            type="text"
            placeholder="Apellido"
            value={formData.lastName}
            onChange={(e) => handleInputChange('lastName', e.target.value)}
            className={`w-full p-3 border-2 rounded-lg transition-all ${errors.lastName ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
          />
          {errors.lastName && <p className="text-red-500 text-xs mt-1">{errors.lastName}</p>}
        </div>
      </div>
      
      <div>
        <input
          type="email"
          placeholder="Correo electrónico"
          value={formData.email}
          onChange={(e) => handleInputChange('email', e.target.value)}
          className={`w-full p-3 border-2 rounded-lg transition-all ${errors.email ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
        />
        {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
      </div>
      
      <div>
        <input
          type="tel"
          placeholder="Teléfono"
          value={formData.phone}
          onChange={(e) => handleInputChange('phone', e.target.value)}
          className={`w-full p-3 border-2 rounded-lg transition-all ${errors.phone ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
        />
        {errors.phone && <p className="text-red-500 text-xs mt-1">{errors.phone}</p>}
      </div>
      
      <div>
        <input
          type="password"
          placeholder="Contraseña"
          value={formData.password}
          onChange={(e) => handleInputChange('password', e.target.value)}
          className={`w-full p-3 border-2 rounded-lg transition-all ${errors.password ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
        />
        {errors.password && <p className="text-red-500 text-xs mt-1">{errors.password}</p>}
      </div>
      
      <div>
        <input
          type="password"
          placeholder="Confirmar contraseña"
          value={formData.confirmPassword}
          onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
          className={`w-full p-3 border-2 rounded-lg transition-all ${errors.confirmPassword ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
        />
        {errors.confirmPassword && <p className="text-red-500 text-xs mt-1">{errors.confirmPassword}</p>}
      </div>
    </div>
  );

  const renderStep2 = () => (
    <div className="space-y-6">
      <h3 className="text-xl font-semibold text-center text-gray-800 mb-6">Elige tu plan</h3>
      
      <div className="space-y-4">
        {plans.map((plan) => (
          <div
            key={plan.id}
            onClick={() => handleInputChange('planType', plan.id)}
            className={`relative p-6 border-2 rounded-xl cursor-pointer transition-all duration-300 ${
              formData.planType === plan.id
                ? 'border-green-400 bg-green-50 shadow-lg transform scale-105'
                : 'border-gray-200 hover:border-green-300 hover:shadow-md'
            } ${plan.popular ? 'ring-2 ring-green-200' : ''}`}
          >
            {plan.popular && (
              <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                <span className="bg-green-500 text-white px-4 py-1 rounded-full text-sm font-semibold">
                  MÁS POPULAR
                </span>
              </div>
            )}
            
            {plan.discount && (
              <div className="absolute top-4 right-4">
                <span className="bg-red-500 text-white px-2 py-1 rounded text-xs font-bold">
                  {plan.discount}
                </span>
              </div>
            )}
            
            <div className="flex items-center justify-between mb-4">
              <div>
                <h4 className="text-lg font-semibold text-gray-800">{plan.name}</h4>
                <div className="flex items-center gap-2">
                  <span className="text-2xl font-bold text-green-600">{plan.price}</span>
                  <span className="text-gray-500">{plan.period}</span>
                  {plan.originalPrice && (
                    <span className="text-sm text-gray-400 line-through">{plan.originalPrice}</span>
                  )}
                </div>
              </div>
              <div className={`w-6 h-6 rounded-full border-2 flex items-center justify-center ${
                formData.planType === plan.id
                  ? 'border-green-500 bg-green-500'
                  : 'border-gray-300'
              }`}>
                {formData.planType === plan.id && <Check size={16} className="text-white" />}
              </div>
            </div>
            
            <ul className="space-y-2">
              {plan.features.map((feature, idx) => (
                <li key={idx} className="flex items-center text-sm text-gray-600">
                  <Check size={16} className="text-green-500 mr-2 flex-shrink-0" />
                  {feature}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
      
      {errors.planType && <p className="text-red-500 text-sm text-center">{errors.planType}</p>}
    </div>
  );

  const renderStep3 = () => (
    <div className="space-y-6">
      <div className="text-center mb-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-2">Información de Pago</h3>
        <div className="flex items-center justify-center text-sm text-gray-600">
          <Shield size={16} className="mr-2 text-green-500" />
          Pago seguro y encriptado
        </div>
      </div>
      
      <div className="bg-gray-50 p-4 rounded-lg mb-6">
        <div className="flex justify-between items-center">
          <span className="text-gray-600">Plan seleccionado:</span>
          <span className="font-semibold">
            {plans.find(p => p.id === formData.planType)?.name} - {plans.find(p => p.id === formData.planType)?.price}
          </span>
        </div>
      </div>
      
      <div className="space-y-4">
        <div>
          <input
            type="text"
            placeholder="Número de tarjeta"
            value={formData.cardNumber}
            onChange={(e) => handleInputChange('cardNumber', formatCardNumber(e.target.value))}
            maxLength="19"
            className={`w-full p-3 border-2 rounded-lg transition-all ${errors.cardNumber ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
          />
          {errors.cardNumber && <p className="text-red-500 text-xs mt-1">{errors.cardNumber}</p>}
        </div>
        
        <div>
          <input
            type="text"
            placeholder="Nombre del titular"
            value={formData.cardName}
            onChange={(e) => handleInputChange('cardName', e.target.value.toUpperCase())}
            className={`w-full p-3 border-2 rounded-lg transition-all ${errors.cardName ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
          />
          {errors.cardName && <p className="text-red-500 text-xs mt-1">{errors.cardName}</p>}
        </div>
        
        <div className="grid grid-cols-2 gap-4">
          <div>
            <input
              type="text"
              placeholder="MM/AA"
              value={formData.expiryDate}
              onChange={(e) => {
                let value = e.target.value.replace(/\D/g, '');
                if (value.length >= 2) {
                  value = value.substring(0, 2) + '/' + value.substring(2, 4);
                }
                handleInputChange('expiryDate', value);
              }}
              maxLength="5"
              className={`w-full p-3 border-2 rounded-lg transition-all ${errors.expiryDate ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
            />
            {errors.expiryDate && <p className="text-red-500 text-xs mt-1">{errors.expiryDate}</p>}
          </div>
          <div>
            <input
              type="text"
              placeholder="CVV"
              value={formData.cvv}
              onChange={(e) => handleInputChange('cvv', e.target.value.replace(/\D/g, ''))}
              maxLength="3"
              className={`w-full p-3 border-2 rounded-lg transition-all ${errors.cvv ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
            />
            {errors.cvv && <p className="text-red-500 text-xs mt-1">{errors.cvv}</p>}
          </div>
        </div>
        
        <div>
          <input
            type="text"
            placeholder="Dirección de facturación"
            value={formData.billingAddress}
            onChange={(e) => handleInputChange('billingAddress', e.target.value)}
            className={`w-full p-3 border-2 rounded-lg transition-all ${errors.billingAddress ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
          />
          {errors.billingAddress && <p className="text-red-500 text-xs mt-1">{errors.billingAddress}</p>}
        </div>
        
        <div className="grid grid-cols-2 gap-4">
          <div>
            <input
              type="text"
              placeholder="Ciudad"
              value={formData.city}
              onChange={(e) => handleInputChange('city', e.target.value)}
              className={`w-full p-3 border-2 rounded-lg transition-all ${errors.city ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
            />
            {errors.city && <p className="text-red-500 text-xs mt-1">{errors.city}</p>}
          </div>
          <div>
            <input
              type="text"
              placeholder="Código postal"
              value={formData.zipCode}
              onChange={(e) => handleInputChange('zipCode', e.target.value)}
              className={`w-full p-3 border-2 rounded-lg transition-all ${errors.zipCode ? 'border-red-400' : 'border-gray-200 focus:border-green-400'} bg-gray-50 focus:bg-white`}
            />
            {errors.zipCode && <p className="text-red-500 text-xs mt-1">{errors.zipCode}</p>}
          </div>
        </div>
      </div>
    </div>
  );

  const renderStep4 = () => (
    <div className="text-center space-y-6">
      <div className="w-20 h-20 bg-green-100 rounded-full flex items-center justify-center mx-auto">
        <Check size={40} className="text-green-500" />
      </div>
      
      <div>
        <h3 className="text-2xl font-bold text-gray-800 mb-2">¡Registro Exitoso!</h3>
        <p className="text-gray-600">
          Bienvenido a CloudFit, {formData.firstName}. Tu cuenta ha sido creada exitosamente.
        </p>
      </div>
      
      <div className="bg-green-50 p-6 rounded-lg">
        <h4 className="font-semibold text-green-800 mb-3">Detalles de tu suscripción:</h4>
        <div className="space-y-2 text-sm">
          <div className="flex justify-between">
            <span>Plan:</span>
            <span className="font-medium">{plans.find(p => p.id === formData.planType)?.name}</span>
          </div>
          <div className="flex justify-between">
            <span>Precio:</span>
            <span className="font-medium">{plans.find(p => p.id === formData.planType)?.price}</span>
          </div>
          <div className="flex justify-between">
            <span>Email:</span>
            <span className="font-medium">{formData.email}</span>
          </div>
        </div>
      </div>
      
      <button
        onClick={() => window.location.reload()}
        className="w-full bg-gradient-to-r from-green-400 to-green-500 text-white py-3 px-6 rounded-lg font-semibold hover:from-green-500 hover:to-green-600 transition-all duration-300 shadow-lg hover:shadow-xl"
      >
        Ir al Dashboard
      </button>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-green-50 flex items-center justify-center p-4">
      <div className="w-full max-w-md bg-white rounded-2xl shadow-2xl overflow-hidden">
        {/* Header */}
        <div className="bg-gradient-to-r from-green-400 to-green-500 p-6 text-white text-center">
          <div className="w-16 h-16 bg-white bg-opacity-20 rounded-2xl flex items-center justify-center mx-auto mb-4">
            <User size={32} />
          </div>
          <h2 className="text-2xl font-bold">CloudFit</h2>
          <p className="text-green-100">Únete a nuestra comunidad</p>
        </div>

        {/* Progress Bar */}
        {currentStep < 4 && (
          <div className="p-6 pb-0">
            {renderProgressBar()}
          </div>
        )}

        {/* Content */}
        <div className="p-6">
          {currentStep === 1 && renderStep1()}
          {currentStep === 2 && renderStep2()}
          {currentStep === 3 && renderStep3()}
          {currentStep === 4 && renderStep4()}
        </div>

        {/* Navigation Buttons */}
        {currentStep < 4 && (
          <div className="p-6 pt-0 flex gap-4">
            {currentStep > 1 && (
              <button
                onClick={prevStep}
                className="flex-1 bg-gray-200 text-gray-700 py-3 px-4 rounded-lg font-semibold hover:bg-gray-300 transition-all duration-300 flex items-center justify-center"
              >
                <ChevronLeft size={20} className="mr-1" />
                Atrás
              </button>
            )}
            
            <button
              onClick={currentStep === 3 ? handleSubmit : nextStep}
              disabled={isLoading}
              className="flex-1 bg-gradient-to-r from-green-400 to-green-500 text-white py-3 px-4 rounded-lg font-semibold hover:from-green-500 hover:to-green-600 transition-all duration-300 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
            >
              {isLoading ? (
                <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin" />
              ) : (
                <>
                  {currentStep === 3 ? 'Completar Registro' : 'Siguiente'}
                  {currentStep < 3 && <ChevronRight size={20} className="ml-1" />}
                </>
              )}
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CloudFitRegistration;
