import React, { useState, useRef, useEffect } from 'react';
import './Header.css';


// Funci贸n para cerrar sesi贸n (siguiendo el patr贸n de login/registro)
  const handleLogout = () => {
    // Confirmar antes de cerrar sesi贸n
    if (window.confirm('驴Est谩s seguro de que quieres cerrar sesi贸n?')) {
      try {
        // Limpiar localStorage (igual que hacen login/registro)
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        
        // Verificar que se limpiaron los datos
        console.log('Sesi贸n cerrada correctamente');
        
        // Redireccionar al login
        navigate('/');
      } catch (error) {
        console.error('Error al cerrar sesi贸n:', error);
        // A煤n as铆 redirigir al login
        navigate('/');
      }
    }
  };
const Header = ({ user, onLogout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuRef = useRef(null);

  // Cerrar men煤 cuando se hace clic fuera
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLogout = () => {
    setIsMenuOpen(false);
    if (onLogout) {
      onLogout();
    }
  };

  return (
    <header className="header">
      <div className="header-left">
        <div className="gym-logo">
          <img src="/cloudfit_l.png" alt="Gym Logo" className="logo-image" />
          <h1 className="gym-name"> CloudFit </h1>
        </div>
      </div>
      
      <div className="header-right">
        <div className="user-info" onClick={toggleMenu} ref={menuRef}>
          <span className="username">{user?.username || 'Usuario'}</span>
          <img 
            src="/usuario_logo.png" 
            alt="Usuario" 
            className="user-avatar"
          />
          
          {/* Men煤 desplegable simple */}
          {isMenuOpen && (
            <div className="user-menu">
              <button className="logout-btn" onClick={handleLogout}>
                <span className="logout-icon"></span>
                <span>Cerrar Sesi贸n</span>
              </button>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
