import React, { useState, useEffect } from 'react';

const UsersManagement = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage] = useState(12);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showUserModal, setShowUserModal] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, []);

  useEffect(() => {
    filterUsers();
  }, [searchTerm, users, filterStatus]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      // Datos de ejemplo expandidos
      const mockUsers = [
        {
          id: '501234',
          username: 'juan_perez',
          email: 'juan.perez@gmail.com',
          fullName: 'Juan Pérez',
          planType: 'annual',
          subscriptionStatus: 'active',
          registrationDate: '2025-06-23',
          phone: '1234567890',
          lastLogin: '2025-06-23',
          avatar: '👨‍💼'
        },
        {
          id: '501235',
          username: 'maria_garcia',
          email: 'maria.garcia@gmail.com',
          fullName: 'María García',
          planType: 'monthly',
          subscriptionStatus: 'active',
          registrationDate: '2025-06-22',
          phone: '0987654321',
          lastLogin: '2025-06-22',
          avatar: '👩‍💼'
        },
        {
          id: '501236',
          username: 'carlos_lopez',
          email: 'carlos.lopez@gmail.com',
          fullName: 'Carlos López',
          planType: 'monthly',
          subscriptionStatus: 'expired',
          registrationDate: '2025-05-15',
          phone: '1122334455',
          lastLogin: '2025-06-20',
          avatar: '👨‍🔧'
        },
        {
          id: '501237',
          username: 'ana_martinez',
          email: 'ana.martinez@gmail.com',
          fullName: 'Ana Martínez',
          planType: 'annual',
          subscriptionStatus: 'active',
          registrationDate: '2025-06-20',
          phone: '5566778899',
          lastLogin: '2025-06-23',
          avatar: '👩‍🎓'
        },
        {
          id: '501238',
          username: 'pedro_sanchez',
          email: 'pedro.sanchez@gmail.com',
          fullName: 'Pedro Sánchez',
          planType: 'monthly',
          subscriptionStatus: 'suspended',
          registrationDate: '2025-05-10',
          phone: '4433221100',
          lastLogin: '2025-06-18',
          avatar: '👨‍🎨'
        },
        {
          id: '501239',
          username: 'lucia_torres',
          email: 'lucia.torres@gmail.com',
          fullName: 'Lucía Torres',
          planType: 'annual',
          subscriptionStatus: 'active',
          registrationDate: '2025-06-15',
          phone: '7788990011',
          lastLogin: '2025-06-23',
          avatar: '👩‍⚕️'
        }
      ];
      
      setUsers(mockUsers);
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const filterUsers = () => {
    let filtered = users;

    // Filtro por término de búsqueda
    if (searchTerm) {
      filtered = filtered.filter(user =>
        user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.fullName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.id.includes(searchTerm)
      );
    }

    // Filtro por estado
    if (filterStatus !== 'all') {
      filtered = filtered.filter(user => user.subscriptionStatus === filterStatus);
    }

    setFilteredUsers(filtered);
    setCurrentPage(1);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleFilterChange = (status) => {
    setFilterStatus(status);
  };

  const handleUserAction = async (userId, action) => {
    try {
      console.log(`Acción ${action} ejecutada en usuario ${userId}`);
      
      setUsers(prevUsers =>
        prevUsers.map(user =>
          user.id === userId
            ? { 
                ...user, 
                subscriptionStatus: action === 'suspend' ? 'suspended' : 
                                 action === 'activate' ? 'active' : 
                                 user.subscriptionStatus 
              }
            : user
        )
      );
    } catch (error) {
      console.error(`Error executing ${action}:`, error);
    }
  };

  const openUserModal = (user) => {
    setSelectedUser(user);
    setShowUserModal(true);
  };

  const closeUserModal = () => {
    setSelectedUser(null);
    setShowUserModal(false);
  };

  // Paginación
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#10b981';
      case 'expired': return '#f59e0b';
      case 'suspended': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'active': return 'Activo';
      case 'expired': return 'Expirado';
      case 'suspended': return 'Suspendido';
      default: return 'Desconocido';
    }
  };

  return (
    <div className="users-management">
      <div className="section-header">
        <h2>Gestión de Usuarios</h2>
        <div className="users-stats">
          <div className="stat-card mini">
            <span className="stat-number">{users.length}</span>
            <span className="stat-label">Total</span>
          </div>
          <div className="stat-card mini">
            <span className="stat-number">{users.filter(u => u.subscriptionStatus === 'active').length}</span>
            <span className="stat-label">Activos</span>
          </div>
          <div className="stat-card mini">
            <span className="stat-number">{users.filter(u => u.subscriptionStatus === 'expired').length}</span>
            <span className="stat-label">Expirados</span>
          </div>
        </div>
      </div>

      <div className="users-controls">
        <div className="search-section">
          <div className="search-bar">
            <span className="search-icon">🔍</span>
            <input
              type="text"
              placeholder="Buscar por nombre, email o ID..."
              value={searchTerm}
              onChange={handleSearchChange}
            />
          </div>
        </div>
        
        <div className="filter-buttons">
          <button 
            className={`filter-btn ${filterStatus === 'all' ? 'active' : ''}`}
            onClick={() => handleFilterChange('all')}
          >
            Todos
          </button>
          <button 
            className={`filter-btn ${filterStatus === 'active' ? 'active' : ''}`}
            onClick={() => handleFilterChange('active')}
          >
            Activos
          </button>
          <button 
            className={`filter-btn ${filterStatus === 'suspended' ? 'active' : ''}`}
            onClick={() => handleFilterChange('suspended')}
          >
            Suspendidos
          </button>
          <button 
            className={`filter-btn ${filterStatus === 'expired' ? 'active' : ''}`}
            onClick={() => handleFilterChange('expired')}
          >
            Expirados
          </button>
        </div>
      </div>

      {loading ? (
        <div className="loading">
          <div className="spinner"></div>
          <p>Cargando usuarios...</p>
        </div>
      ) : (
        <>
          <div className="users-grid">
            {currentUsers.map(user => (
              <div key={user.id} className="user-card">
                <div className="user-card-header">
                  <div className="user-avatar-section">
                    <div className="user-avatar-large">{user.avatar}</div>
                    <div className="user-info-primary">
                      <h4 className="user-full-name">{user.fullName}</h4>
                      <p className="user-username">@{user.username}</p>
                      <p className="user-id">ID: {user.id}</p>
                    </div>
                  </div>
                  <div className="user-status-section">
                    <span 
                      className="status-dot"
                      style={{ backgroundColor: getStatusColor(user.subscriptionStatus) }}
                    ></span>
                    <span className="status-text">{getStatusText(user.subscriptionStatus)}</span>
                  </div>
                </div>

                <div className="user-card-body">
                  <div className="user-detail-row">
                    <span className="detail-icon">📧</span>
                    <span className="detail-text">{user.email}</span>
                  </div>
                  <div className="user-detail-row">
                    <span className="detail-icon">📱</span>
                    <span className="detail-text">{user.phone}</span>
                  </div>
                  <div className="user-detail-row">
                    <span className="detail-icon">💳</span>
                    <span className="detail-text">
                      Plan {user.planType === 'annual' ? 'Anual' : 'Mensual'}
                    </span>
                  </div>
                  <div className="user-detail-row">
                    <span className="detail-icon">🕒</span>
                    <span className="detail-text">
                      Último login: {new Date(user.lastLogin).toLocaleDateString()}
                    </span>
                  </div>
                </div>

                <div className="user-card-footer">
                  <button
                    className="action-btn view-btn"
                    onClick={() => openUserModal(user)}
                    title="Ver detalles"
                  >
                    <span>👁️</span>
                    Ver
                  </button>
                  <button
                    className="action-btn edit-btn"
                    onClick={() => console.log('Editar', user.id)}
                    title="Editar"
                  >
                    <span>✏️</span>
                    Editar
                  </button>
                  {user.subscriptionStatus === 'active' ? (
                    <button
                      className="action-btn suspend-btn"
                      onClick={() => handleUserAction(user.id, 'suspend')}
                      title="Suspender"
                    >
                      <span>⏸️</span>
                      Suspender
                    </button>
                  ) : user.subscriptionStatus === 'suspended' ? (
                    <button
                      className="action-btn activate-btn"
                      onClick={() => handleUserAction(user.id, 'activate')}
                      title="Activar"
                    >
                      <span>▶️</span>
                      Activar
                    </button>
                  ) : (
                    <button
                      className="action-btn renew-btn"
                      onClick={() => handleUserAction(user.id, 'renew')}
                      title="Renovar"
                    >
                      <span>🔄</span>
                      Renovar
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>

          {filteredUsers.length === 0 && (
            <div className="empty-state">
              <div className="empty-icon">🔍</div>
              <h3>No se encontraron usuarios</h3>
              <p>Intenta ajustar los filtros de búsqueda</p>
            </div>
          )}

          {/* Paginación */}
          {totalPages > 1 && (
            <div className="pagination">
              <button
                className="page-btn"
                onClick={() => paginate(currentPage - 1)}
                disabled={currentPage === 1}
              >
                ← Anterior
              </button>
              
              <div className="page-numbers">
                {Array.from({ length: Math.min(totalPages, 5) }, (_, i) => {
                  let pageNum;
                  if (totalPages <= 5) {
                    pageNum = i + 1;
                  } else if (currentPage <= 3) {
                    pageNum = i + 1;
                  } else if (currentPage >= totalPages - 2) {
                    pageNum = totalPages - 4 + i;
                  } else {
                    pageNum = currentPage - 2 + i;
                  }
                  
                  return (
                    <button
                      key={pageNum}
                      className={`page-number ${currentPage === pageNum ? 'active' : ''}`}
                      onClick={() => paginate(pageNum)}
                    >
                      {pageNum}
                    </button>
                  );
                })}
              </div>
              
              <button
                className="page-btn"
                onClick={() => paginate(currentPage + 1)}
                disabled={currentPage === totalPages}
              >
                Siguiente →
              </button>
            </div>
          )}

          <div className="pagination-info">
            Mostrando {indexOfFirstUser + 1} a {Math.min(indexOfLastUser, filteredUsers.length)} de {filteredUsers.length} usuarios
          </div>
        </>
      )}

      {/* Modal de detalles del usuario */}
      {showUserModal && selectedUser && (
        <div className="modal-overlay" onClick={closeUserModal}>
          <div className="modal-content user-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <div className="modal-user-header">
                <div className="modal-avatar">{selectedUser.avatar}</div>
                <div>
                  <h3>{selectedUser.fullName}</h3>
                  <p className="modal-username">@{selectedUser.username}</p>
                </div>
              </div>
              <button className="close-btn" onClick={closeUserModal}>✕</button>
            </div>
            
            <div className="modal-body">
              <div className="user-details-grid">
                <div className="detail-section">
                  <h4>Información Personal</h4>
                  <div className="detail-items">
                    <div className="detail-item">
                      <span className="detail-label">ID de Usuario:</span>
                      <span className="detail-value">{selectedUser.id}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Email:</span>
                      <span className="detail-value">{selectedUser.email}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Teléfono:</span>
                      <span className="detail-value">{selectedUser.phone}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Fecha de Registro:</span>
                      <span className="detail-value">{new Date(selectedUser.registrationDate).toLocaleDateString()}</span>
                    </div>
                  </div>
                </div>
                
                <div className="detail-section">
                  <h4>Suscripción</h4>
                  <div className="detail-items">
                    <div className="detail-item">
                      <span className="detail-label">Plan:</span>
                      <span className="detail-value">
                        <span className={`plan-badge ${selectedUser.planType}`}>
                          {selectedUser.planType === 'annual' ? 'Anual' : 'Mensual'}
                        </span>
                      </span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Estado:</span>
                      <span className="detail-value">
                        <span 
                          className="status-badge-modal"
                          style={{ backgroundColor: getStatusColor(selectedUser.subscriptionStatus) }}
                        >
                          {getStatusText(selectedUser.subscriptionStatus)}
                        </span>
                      </span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Último Login:</span>
                      <span className="detail-value">{new Date(selectedUser.lastLogin).toLocaleDateString()}</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            
            <div className="modal-footer">
              <button className="btn-secondary" onClick={closeUserModal}>Cerrar</button>
              <button className="btn-primary">Editar Usuario</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UsersManagement;
