import React, { useState, useEffect } from 'react';
import MachineDetail from './MachineDetail'; // Importar el nuevo componente
import './mapa.css';

const MapSection = () => {
  const [selectedMachine, setSelectedMachine] = useState(null);
  const [machines, setMachines] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Cargar m√°quinas de la base de datos
  useEffect(() => {
    fetchMachines();
  }, []);

  const fetchMachines = async () => {
    setLoading(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/maquinas/all', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      const data = await response.json();
      console.log('Datos del servidor:', data);
      
      if (response.ok && data.success) {
        // Mapear datos de la BD al formato del mapa
        const mappedMachines = data.machines.map((machine, index) => {
          // Calcular posici√≥n seg√∫n categor√≠a
          let x, y;
          const categoryMachines = data.machines.filter(m => m.categoria === machine.categoria);
          const categoryIndex = categoryMachines.findIndex(m => m.machineId === machine.machineId);
          
          switch (machine.categoria) {
            case 'cardio':
              x = 15 + (categoryIndex * 10);
              y = 20 + (categoryIndex * 15);
              break;
            case 'fuerza':
              x = 55 + (categoryIndex * 10);
              y = 15 + (categoryIndex * 15);
              break;
            case 'libre':
              x = 85 + (categoryIndex * 5);
              y = 20 + (categoryIndex * 15);
              break;
            case 'funcional':
              x = 45 + (categoryIndex * 10);
              y = 70 + (categoryIndex * 5);
              break;
            default:
              x = 50;
              y = 50;
          }

          return {
            id: machine.machineId,
            name: machine.nombre,
            type: machine.categoria,
            x: Math.min(x, 95), // L√≠mite derecho
            y: Math.min(y, 85), // L√≠mite inferior
            image: `http://localhost:5000/uploads/${machine.nombreImagen}`,
            video: `http://localhost:5000/uploads/${machine.nombreVideo}`,
            description: `${machine.nombre} - ${machine.pesos}`,
            instructions: `M√°quina de ${machine.categoria}. Cantidad: ${machine.cantidad}`,
            cantidad: machine.cantidad,
            pesos: machine.pesos,
            status: machine.status,
            categoria: machine.categoria
          };
        });
        
        setMachines(mappedMachines);
      } else {
        setError(data.message || 'Error al cargar m√°quinas');
      }
    } catch (error) {
      console.error('Error fetching machines:', error);
      setError('Error de conexi√≥n con el servidor');
    } finally {
      setLoading(false);
    }
  };

  const getMachineColor = (type) => {
    switch (type) {
      case 'cardio': return '#e74c3c';
      case 'fuerza': return '#9b59b6';
      case 'libre': return '#f39c12';
      case 'funcional': return '#3498db';
      default: return '#95a5a6';
    }
  };

  const getMachineIcon = (type) => {
    switch (type) {
      case 'cardio': return 'üèÉ';
      case 'fuerza': return 'üèãÔ∏è';
      case 'libre': return 'üí™';
      case 'funcional': return 'ü§∏';
      default: return '‚ö°';
    }
  };

  // Estad√≠sticas din√°micas
  const stats = {
    total: machines.reduce((sum, machine) => sum + (machine.cantidad || 1), 0),
    cardio: machines.filter(m => m.type === 'cardio').length,
    fuerza: machines.filter(m => m.type === 'fuerza').length,
    libre: machines.filter(m => m.type === 'libre').length,
    funcional: machines.filter(m => m.type === 'funcional').length
  };

  if (loading) {
    return (
      <div className="map-section">
        <div className="loading">
          <div className="spinner"></div>
          <p>Cargando mapa del gimnasio...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="map-section">
        <div className="error-message" style={{ 
          background: '#fee2e2', 
          color: '#dc2626', 
          padding: '20px', 
          borderRadius: '8px', 
          textAlign: 'center' 
        }}>
          <p>{error}</p>
          <button 
            onClick={fetchMachines}
            style={{
              background: '#dc2626',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '5px',
              marginTop: '10px',
              cursor: 'pointer'
            }}
          >
            Reintentar
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="map-section">
      <div className="map-header">
        <h2>üó∫Ô∏è Mapa del Gimnasio</h2>
        <div className="legend">
          <div className="legend-item">
            <span className="legend-color cardio"></span>
            <span>Cardio ({stats.cardio})</span>
          </div>
          <div className="legend-item">
            <span className="legend-color strength"></span>
            <span>Fuerza ({stats.fuerza})</span>
          </div>
          <div className="legend-item">
            <span className="legend-color weights"></span>
            <span>Pesos Libres ({stats.libre})</span>
          </div>
          <div className="legend-item">
            <span className="legend-color functional"></span>
            <span>Funcional ({stats.funcional})</span>
          </div>
        </div>
      </div>

      <div className="gym-map">
        <div className="gym-floor">
          
          {/* √Åreas de fondo */}
          <div className="floor-area cardio-area">
            <span className="area-label">ZONA CARDIO</span>
          </div>
          <div className="floor-area strength-area">
            <span className="area-label">ZONA DE FUERZA</span>
          </div>
          <div className="floor-area weights-area">
            <span className="area-label">PESOS LIBRES</span>
          </div>
          <div className="floor-area functional-area">
            <span className="area-label">√ÅREA FUNCIONAL</span>
          </div>
          <div className="floor-area entrance-area">
            <span className="area-label">ENTRADA</span>
          </div>
          <div className="floor-area services-area">
            <span className="area-label">SERVICIOS</span>
          </div>

          {/* M√°quinas din√°micas de la base de datos */}
          {machines.map((machine) => (
            <div
              key={machine.id}
              className="machine-marker"
              style={{
                left: `${machine.x}%`,
                top: `${machine.y}%`,
                backgroundColor: getMachineColor(machine.type),
                opacity: machine.status === 'available' ? 1 : 0.6
              }}
              onClick={() => setSelectedMachine(machine)}
              title={`${machine.name} - ${machine.status === 'available' ? 'Disponible' : 'No disponible'}`}
            >
              <span className="machine-icon">{getMachineIcon(machine.type)}</span>
              <span className="machine-label">{machine.name}</span>
              {machine.cantidad > 1 && (
                <span className="machine-count" style={{
                  position: 'absolute',
                  top: '-5px',
                  right: '-5px',
                  background: '#fff',
                  borderRadius: '50%',
                  padding: '2px 6px',
                  fontSize: '10px',
                  fontWeight: 'bold'
                }}>
                  x{machine.cantidad}
                </span>
              )}
            </div>
          ))}

          {/* Elementos estructurales */}
          <div className="gym-element reception">
            <span className="element-label">Recepci√≥n</span>
          </div>
          <div className="gym-element lockers">
            <span className="element-label">Vestidores</span>
          </div>
          <div className="gym-element bathrooms">
            <span className="element-label">Ba√±os</span>
          </div>
          <div className="gym-element emergency-exit">
            <span className="element-label">Salida Emergencia</span>
          </div>

        </div>
      </div>

      {/* Mostrar componente de detalle si hay m√°quina seleccionada */}
      {selectedMachine && (
        <MachineDetail 
          machine={selectedMachine} 
          onClose={() => setSelectedMachine(null)}
        />
      )}
    </div>
  );
};

