//EmployeesManagement.jsx
import React, { useState, useEffect } from 'react';

const EmployeesManagement = () => {
  const [employees, setEmployees] = useState([]);
  const [filteredEmployees, setFilteredEmployees] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [positionFilter, setPositionFilter] = useState('all');
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    position: 'Barbero',
    salary: '',
    startDate: '',
    status: 'active'
  });

  useEffect(() => {
    fetchEmployees();
  }, []);

  useEffect(() => {
    filterEmployees();
  }, [employees, searchTerm, statusFilter, positionFilter]);

  const fetchEmployees = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/empleados/all', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      const data = await response.json();
      
      if (data.success) {
        setEmployees(data.employees);
      } else {
        console.error('Error:', data.message);
      }
    } catch (error) {
      console.error('Error fetching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  const filterEmployees = () => {
    let filtered = employees;

    // Filtro de bÃºsqueda
    if (searchTerm) {
      filtered = filtered.filter(employee =>
        employee.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.employeeId.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filtro de estado
    if (statusFilter !== 'all') {
      filtered = filtered.filter(employee => employee.status === statusFilter);
    }

    // Filtro de puesto
    if (positionFilter !== 'all') {
      filtered = filtered.filter(employee => employee.position === positionFilter);
    }

    setFilteredEmployees(filtered);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      
      if (editingEmployee) {
        // Actualizar empleado existente
        const response = await fetch(`/api/empleados/${editingEmployee.employeeId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(formData)
        });
        
        const data = await response.json();
        
        if (data.success) {
          await fetchEmployees(); // Recargar lista
          alert('Empleado actualizado exitosamente');
        } else {
          alert(data.message || 'Error al actualizar empleado');
        }
      } else {
        // Crear nuevo empleado
        const response = await fetch('/api/empleados/create', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(formData)
        });
        
        const data = await response.json();
        
        if (data.success) {
          await fetchEmployees(); // Recargar lista
          alert(`Empleado creado exitosamente!\nID: ${data.employee.employeeId}\nContraseÃ±a temporal: ${data.employee.temporaryPassword}`);
        } else {
          alert(data.message || 'Error al crear empleado');
        }
      }
      
      resetForm();
      setShowAddModal(false);
      setEditingEmployee(null);
    } catch (error) {
      console.error('Error saving employee:', error);
      alert('Error al guardar empleado');
    }
  };

  const resetForm = () => {
    setFormData({
      firstName: '',
      lastName: '',
      phone: '',
      position: 'Barbero',
      salary: '',
      startDate: '',
      status: 'active'
    });
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    setFormData({
      firstName: employee.firstName,
      lastName: employee.lastName,
      phone: employee.phone,
      position: employee.position,
      salary: employee.salary.toString(),
      startDate: new Date(employee.startDate).toISOString().split('T')[0],
      status: employee.status
    });
    setShowAddModal(true);
  };

  const handleStatusChange = async (employeeId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/empleados/${employeeId}/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ status: newStatus })
      });
      
      const data = await response.json();
      
      if (data.success) {
        await fetchEmployees(); // Recargar lista
        alert(`Empleado ${newStatus === 'active' ? 'activado' : 'desactivado'} exitosamente`);
      } else {
        alert(data.message || 'Error al cambiar estado');
      }
    } catch (error) {
      console.error('Error updating employee status:', error);
      alert('Error al cambiar estado del empleado');
    }
  };

  const handleDelete = async (employeeId) => {
    if (window.confirm('Â¿EstÃ¡s seguro de que quieres eliminar este empleado? Esta acciÃ³n no se puede deshacer.')) {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`/api/empleados/${employeeId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        
        const data = await response.json();
        
        if (data.success) {
          await fetchEmployees(); // Recargar lista
          alert('Empleado eliminado exitosamente');
        } else {
          alert(data.message || 'Error al eliminar empleado');
        }
      } catch (error) {
        console.error('Error deleting employee:', error);
        alert('Error al eliminar empleado');
      }
    }
  };

  const openAddModal = () => {
    resetForm();
    setEditingEmployee(null);
    setShowAddModal(true);
  };

  const closeModal = () => {
    setShowAddModal(false);
    setEditingEmployee(null);
    resetForm();
  };

  const getPositionIcon = (position) => {
    const icons = {
      'Barbero': 'âœ‚ï¸',
      'Estilista': 'ðŸ’‡â€â™€ï¸',
      'Coach': 'ðŸ‹ï¸â€â™‚ï¸'
    };
    return icons[position] || 'ðŸ‘¨â€ðŸ’¼';
  };

  return (
    <div className="employees-management">
      <div className="section-header">
        <h2>GestiÃ³n de Empleados</h2>
        <div className="users-stats">
          <div className="stat-card mini">
            <span className="stat-number">{employees.length}</span>
            <span className="stat-label">Total</span>
          </div>
          <div className="stat-card mini">
            <span className="stat-number">{employees.filter(emp => emp.status === 'active').length}</span>
            <span className="stat-label">Activos</span>
          </div>
          <div className="stat-card mini">
            <span className="stat-number">{employees.filter(emp => emp.status === 'inactive').length}</span>
            <span className="stat-label">Inactivos</span>
          </div>
        </div>
      </div>

      <div className="users-controls">
        <div className="search-section">
          <div className="search-bar">
            <span className="search-icon">ðŸ”</span>
            <input
              type="text"
              placeholder="Buscar empleado por nombre, email o ID..."
              value={searchTerm}
              onChange={handleSearchChange}
            />
          </div>
        </div>
        
        <div className="filter-buttons">
          <button 
            className={`filter-btn ${statusFilter === 'all' ? 'active' : ''}`}
            onClick={() => setStatusFilter('all')}
          >
            Todos
          </button>
          <button 
            className={`filter-btn ${statusFilter === 'active' ? 'active' : ''}`}
            onClick={() => setStatusFilter('active')}
          >
            Activos
          </button>
          <button 
            className={`filter-btn ${statusFilter === 'inactive' ? 'active' : ''}`}
            onClick={() => setStatusFilter('inactive')}
          >
            Inactivos
          </button>
          <button 
            className={`filter-btn ${positionFilter === 'Barbero' ? 'active' : ''}`}
            onClick={() => setPositionFilter(positionFilter === 'Barbero' ? 'all' : 'Barbero')}
          >
            Barberos
          </button>
          <button 
            className={`filter-btn ${positionFilter === 'Estilista' ? 'active' : ''}`}
            onClick={() => setPositionFilter(positionFilter === 'Estilista' ? 'all' : 'Estilista')}
          >
            Estilistas
          </button>
          <button 
            className={`filter-btn ${positionFilter === 'Coach' ? 'active' : ''}`}
            onClick={() => setPositionFilter(positionFilter === 'Coach' ? 'all' : 'Coach')}
          >
            Coaches
          </button>
        </div>
      </div>

      <div style={{ marginBottom: 'var(--spacing-lg)', display: 'flex', justifyContent: 'flex-end' }}>
        <button className="btn-primary" onClick={openAddModal}>
          + Nuevo Empleado
        </button>
      </div>

      {loading ? (
        <div className="loading">
          <div className="spinner"></div>
          <p>Cargando empleados...</p>
        </div>
      ) : (
        <div className="users-grid">
          {filteredEmployees.length === 0 ? (
            <div className="no-results">
              <p>No se encontraron empleados</p>
            </div>
          ) : (
            filteredEmployees.map(employee => (
              <div key={employee.employeeId} className="user-card">
                <div className="user-card-header">
                  <div className="user-avatar-section">
                    <div className="user-avatar-large">
                      {getPositionIcon(employee.position)}
                    </div>
                    <div className="user-info-primary">
                      <h4 className="user-full-name">{employee.firstName} {employee.lastName}</h4>
                      <p className="user-username">@{employee.username}</p>
                      <p className="user-id">ID: {employee.employeeId}</p>
                    </div>
                  </div>
                  <div className="user-status-section">
                    <span 
                      className="status-dot"
                      style={{ backgroundColor: employee.status === 'active' ? '#10b981' : '#ef4444' }}
                    ></span>
                    <span className="status-text">{employee.status === 'active' ? 'Activo' : 'Inactivo'}</span>
                  </div>
                </div>

                <div className="user-card-body">
                  <div className="user-detail-row">
                    <span className="detail-icon">ðŸ“§</span>
                    <span className="detail-text">{employee.email}</span>
                  </div>
                  <div className="user-detail-row">
                    <span className="detail-icon">ðŸ“±</span>
                    <span className="detail-text">{employee.phone}</span>
