import React, { useState, useEffect } from 'react';


const EmployeesManagement = () => {
  const [employees, setEmployees] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    role: 'instructor',
    salary: '',
    startDate: '',
    status: 'active'
  });

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    setLoading(true);
    try {
      // AquÃ­ harÃ­as la llamada a tu API
      // const response = await fetch('/api/admin/employees');
      // const employeesData = await response.json();
      
      // Datos de ejemplo
      const mockEmployees = [
        {
          id: 'EMP001',
          firstName: 'Carlos',
          lastName: 'Mendoza',
          email: 'carlos.mendoza@cloudfit.com',
          phone: '5551234567',
          role: 'instructor',
          salary: 25000,
          startDate: '2024-01-15',
          status: 'active'
        },
        {
          id: 'EMP002',
          firstName: 'Ana',
          lastName: 'LÃ³pez',
          email: 'ana.lopez@cloudfit.com',
          phone: '5559876543',
          role: 'receptionist',
          salary: 18000,
          startDate: '2024-03-10',
          status: 'active'
        },
        {
          id: 'EMP003',
          firstName: 'Roberto',
          lastName: 'Silva',
          email: 'roberto.silva@cloudfit.com',
          phone: '5555555555',
          role: 'instructor',
          salary: 23000,
          startDate: '2023-11-20',
          status: 'inactive'
        }
      ];
      
      setEmployees(mockEmployees);
    } catch (error) {
      console.error('Error fetching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingEmployee) {
        // Actualizar empleado existente
        // await fetch(`/api/admin/employees/${editingEmployee.id}`, {
        //   method: 'PUT',
        //   headers: { 'Content-Type': 'application/json' },
        //   body: JSON.stringify(formData)
        // });
        
        setEmployees(prev =>
          prev.map(emp =>
            emp.id === editingEmployee.id
              ? { ...emp, ...formData }
              : emp
          )
        );
      } else {
        // Crear nuevo empleado
        const newEmployee = {
          id: `EMP${String(employees.length + 1).padStart(3, '0')}`,
          ...formData
        };
        
        // await fetch('/api/admin/employees', {
        //   method: 'POST',
        //   headers: { 'Content-Type': 'application/json' },
        //   body: JSON.stringify(newEmployee)
        // });
        
        setEmployees(prev => [...prev, newEmployee]);
      }
      
      resetForm();
      setShowAddModal(false);
      setEditingEmployee(null);
    } catch (error) {
      console.error('Error saving employee:', error);
    }
  };

  const resetForm = () => {
    setFormData({
      firstName: '',
      lastName: '',
      email: '',
      phone: '',
      role: 'instructor',
      salary: '',
      startDate: '',
      status: 'active'
    });
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    setFormData(employee);
    setShowAddModal(true);
  };

  const handleStatusChange = async (employeeId, newStatus) => {
    try {
      // await fetch(`/api/admin/employees/${employeeId}/status`, {
      //   method: 'PATCH',
      //   headers: { 'Content-Type': 'application/json' },
      //   body: JSON.stringify({ status: newStatus })
      // });
      
      setEmployees(prev =>
        prev.map(emp =>
          emp.id === employeeId
            ? { ...emp, status: newStatus }
            : emp
        )
      );
    } catch (error) {
      console.error('Error updating employee status:', error);
    }
  };

  const handleDelete = async (employeeId) => {
    if (window.confirm('Â¿EstÃ¡s seguro de que quieres eliminar este empleado?')) {
      try {
        // await fetch(`/api/admin/employees/${employeeId}`, { method: 'DELETE' });
        
        setEmployees(prev => prev.filter(emp => emp.id !== employeeId));
      } catch (error) {
        console.error('Error deleting employee:', error);
      }
    }
  };

  const openAddModal = () => {
    resetForm();
    setEditingEmployee(null);
    setShowAddModal(true);
  };

  const closeModal = () => {
    setShowAddModal(false);
    setEditingEmployee(null);
    resetForm();
  };

  const getRoleDisplayName = (role) => {
    const roles = {
      instructor: 'Instructor',
      receptionist: 'Recepcionista',
      manager: 'Gerente',
      admin: 'Administrador',
      maintenance: 'Mantenimiento'
    };
    return roles[role] || role;
  };

  return (
    <div className="employees-management">
      <div className="section-header">
        <h2>GestiÃ³n de Empleados</h2>
        <div className="users-stats">
          <div className="stat-card mini">
            <span className="stat-number">{employees.length}</span>
            <span className="stat-label">Total</span>
          </div>
          <div className="stat-card mini">
            <span className="stat-number">{employees.filter(emp => emp.status === 'active').length}</span>
            <span className="stat-label">Activos</span>
          </div>
          <div className="stat-card mini">
            <span className="stat-number">{employees.filter(emp => emp.status === 'inactive').length}</span>
            <span className="stat-label">Inactivos</span>
          </div>
        </div>
      </div>

      <div className="users-controls">
        <div className="search-section">
          <div className="search-bar">
            <span className="search-icon">ğŸ”</span>
            <input
              type="text"
              placeholder="Buscar empleado por nombre, email o ID..."
              // value={searchTerm}
              // onChange={handleSearchChange}
            />
          </div>
        </div>
        
        <div className="filter-buttons">
          <button className="filter-btn active">Todos</button>
          <button className="filter-btn">Activos</button>
          <button className="filter-btn">Inactivos</button>
          <button className="filter-btn">Instructores</button>
        </div>
      </div>

      <div style={{ marginBottom: 'var(--spacing-lg)', display: 'flex', justifyContent: 'flex-end' }}>
        <button className="btn-primary" onClick={openAddModal}>
          + Nuevo Empleado
        </button>
      </div>

      {loading ? (
        <div className="loading">
          <div className="spinner"></div>
          <p>Cargando empleados...</p>
        </div>
      ) : (
        <div className="users-grid">
          {employees.map(employee => (
            <div key={employee.id} className="user-card">
              <div className="user-card-header">
                <div className="user-avatar-section">
                  <div className="user-avatar-large">
                    {employee.role === 'instructor' && 'ğŸ‹ï¸â€â™‚ï¸'}
                    {employee.role === 'receptionist' && 'ğŸ‘©â€ğŸ’¼'}
                    {employee.role === 'manager' && 'ğŸ‘¨â€ğŸ’¼'}
                    {employee.role === 'admin' && 'ğŸ‘¨â€ğŸ’»'}
                    {employee.role === 'maintenance' && 'ğŸ”§'}
                  </div>
                  <div className="user-info-primary">
                    <h4 className="user-full-name">{employee.firstName} {employee.lastName}</h4>
                    <p className="user-username">@{employee.firstName.toLowerCase()}_{employee.lastName.toLowerCase()}</p>
                    <p className="user-id">ID: {employee.id}</p>
                  </div>
                </div>
                <div className="user-status-section">
                  <span 
                    className="status-dot"
                    style={{ backgroundColor: employee.status === 'active' ? '#10b981' : '#ef4444' }}
                  ></span>
                  <span className="status-text">{employee.status === 'active' ? 'Activo' : 'Inactivo'}</span>
                </div>
              </div>

              <div className="user-card-body">
                <div className="user-detail-row">
                  <span className="detail-icon">ğŸ“§</span>
                  <span className="detail-text">{employee.email}</span>
                </div>
                <div className="user-detail-row">
                  <span className="detail-icon">ğŸ“±</span>
                  <span className="detail-text">{employee.phone}</span>
                </div>
                <div className="user-detail-row">
                  <span className="detail-icon">ğŸ’¼</span>
                  <span className="detail-text">{getRoleDisplayName(employee.role)}</span>
                </div>
                <div className="user-detail-row">
                  <span className="detail-icon">ğŸ’°</span>
                  <span className="detail-text">Salario: ${employee.salary?.toLocaleString()}</span>
                </div>
                <div className="user-detail-row">
                  <span className="detail-icon">ğŸ“…</span>
                  <span className="detail-text">
                    Inicio: {new Date(employee.startDate).toLocaleDateString()}
                  </span>
                </div>
              </div>

              <div className="user-card-footer">
                <button
                  className="action-btn view-btn"
                  onClick={() => handleEdit(employee)}
                  title="Editar"
                >
                  <span>âœï¸</span>
                  Editar
                </button>
                
                {employee.status === 'active' ? (
                  <button
                    className="action-btn suspend-btn"
                    onClick={() => handleStatusChange(employee.id, 'inactive')}
                    title="Desactivar"
                  >
                    <span>â¸ï¸</span>
                    Suspender
                  </button>
                ) : (
                  <button
                    className="action-btn activate-btn"
                    onClick={() => handleStatusChange(employee.id, 'active')}
                    title="Activar"
                  >
                    <span>â–¶ï¸</span>
                    Activar
                  </button>
                )}
                
                <button
                  className="action-btn suspend-btn"
                  onClick={() => handleDelete(employee.id)}
                  title="Eliminar"
                >
                  <span>ğŸ—‘ï¸</span>
                  Eliminar
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Modal para agregar/editar empleado */}
      {showAddModal && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>{editingEmployee ? 'Editar Empleado' : 'Nuevo Empleado'}</h3>
              <button className="close-btn" onClick={closeModal}>âœ•</button>
            </div>
            
            <div className="modal-body">
              <form onSubmit={handleSubmit} className="employee-form">
                <div className="form-row">
                  <div className="form-group">
                    <label>Nombre</label>
                    <input
                      type="text"
                      name="firstName"
                      value={formData.firstName}
                      onChange={handleInputChange}
                      required
                      placeholder="Nombre del empleado"
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>Apellido</label>
                    <input
                      type="text"
                      name="lastName"
                      value={formData.lastName}
                      onChange={handleInputChange}
                      required
                      placeholder="Apellido del empleado"
                    />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Email</label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      required
                      placeholder="email@cloudfit.com"
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>TelÃ©fono</label>
                    <input
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      required
                      placeholder="5551234567"
                    />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Puesto</label>
                    <select
                      name="role"
                      value={formData.role}
                      onChange={handleInputChange}
                      required
                    >
                      <option value="instructor">Instructor</option>
                      <option value="receptionist">Recepcionista</option>
                      <option value="manager">Gerente</option>
                      <option value="admin">Administrador</option>
                      <option value="maintenance">Mantenimiento</option>
                    </select>
                  </div>
                  
                  <div className="form-group">
                    <label>Salario Mensual</label>
                    <input
                      type="number"
                      name="salary"
                      value={formData.salary}
                      onChange={handleInputChange}
                      required
                      placeholder="25000"
                      min="1"
                    />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Fecha de Inicio</label>
                    <input
                      type="date"
                      name="startDate"
                      value={formData.startDate}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>Estado</label>
                    <select
                      name="status"
                      value={formData.status}
                      onChange={handleInputChange}
                      required
                    >
                      <option value="active">Activo</option>
                      <option value="inactive">Inactivo</option>
                    </select>
                  </div>
                </div>
              </form>
            </div>

            <div className="modal-footer">
              <button type="button" className="btn-secondary" onClick={closeModal}>
                Cancelar
              </button>
              <button type="submit" className="btn-primary" onClick={handleSubmit}>
                {editingEmployee ? 'Actualizar' : 'Crear'} Empleado
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default EmployeesManagement;
