import React, { useState } from 'react';

const Signup = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    // Datos iniciales
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    phone: '',
    
    // Plan seleccionado
    planType: '',
    
    // Datos de tarjeta
    cardNumber: '',
    cardName: '',
    expiryDate: '',
    cvv: '',
    billingAddress: '',
    city: '',
    postalCode: ''
  });
  
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [userId, setUserId] = useState('');

  const generateUserId = () => {
    // Genera ID de 6 números que empiece con 50
    const randomDigits = Math.floor(Math.random() * 10000).toString().padStart(4, '0');
    return `50${randomDigits}`;
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
    setError('');
  };

  const validateStep1 = () => {
    const { username, email, password, confirmPassword, firstName, lastName, phone } = formData;
    
    if (!username || !email || !password || !confirmPassword || !firstName || !lastName || !phone) {
      setError('Todos los campos son obligatorios');
      return false;
    }
    
    if (password !== confirmPassword) {
      setError('Las contraseñas no coinciden');
      return false;
    }
    
    if (password.length < 6) {
      setError('La contraseña debe tener al menos 6 caracteres');
      return false;
    }
    
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Ingresa un correo electrónico válido');
      return false;
    }
    
    return true;
  };

  const validateStep2 = () => {
    if (!formData.planType) {
      setError('Selecciona un plan de suscripción');
      return false;
    }
    return true;
  };

  const validateStep3 = () => {
    const { cardNumber, cardName, expiryDate, cvv, billingAddress, city, postalCode } = formData;
    
    if (!cardNumber || !cardName || !expiryDate || !cvv || !billingAddress || !city || !postalCode) {
      setError('Todos los campos de pago son obligatorios');
      return false;
    }
    
    if (cardNumber.replace(/\s/g, '').length !== 16) {
      setError('El número de tarjeta debe tener 16 dígitos');
      return false;
    }
    
    if (cvv.length !== 3) {
      setError('El CVV debe tener 3 dígitos');
      return false;
    }
    
    return true;
  };

  const nextStep = () => {
    let isValid = false;
    
    switch(currentStep) {
      case 1:
        isValid = validateStep1();
        break;
      case 2:
        isValid = validateStep2();
        break;
      case 3:
        isValid = validateStep3();
        break;
      default:
        isValid = true;
    }
    
    if (isValid) {
      setCurrentStep(prev => prev + 1);
      setError('');
    }
  };

  const prevStep = () => {
    setCurrentStep(prev => prev - 1);
    setError('');
  };

  const handleSubmit = async () => {
    if (!validateStep3()) return;

    try {
      const newUserId = generateUserId();
      
      const response = await fetch('http://18.219.156.200:5000/api/auth/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...formData,
          userId: newUserId
        })
      });

      const data = await response.json();

      if (response.ok) {
        setUserId(newUserId);
        setSuccess(`¡Cuenta creada exitosamente! Tu ID de usuario es: ${newUserId}`);
        setError('');
      } else {
        setError(data.message || 'Error al crear la cuenta');
        setSuccess('');
      }
    } catch (error) {
      setError('Error de conexión al servidor.');
      setSuccess('');
    }
  };

  const formatCardNumber = (value) => {
    const v = value.replace(/\s+/g, '').replace(/[^0-9]/gi, '');
    const matches = v.match(/\d{4,16}/g);
    const match = matches && matches[0] || '';
    const parts = [];
    for (let i = 0, len = match.length; i < len; i += 4) {
      parts.push(match.substring(i, i + 4));
    }
    if (parts.length) {
      return parts.join(' ');
    } else {
      return v;
    }
  };

  const formatExpiryDate = (value) => {
    const v = value.replace(/\s+/g, '').replace(/[^0-9]/gi, '');
    if (v.length >= 2) {
      return `${v.substring(0, 2)}/${v.substring(2, 4)}`;
    }
    return v;
  };

  // Componente Step 1 - Datos Iniciales
  const Step1 = () => (
    <div className="step-content">
      <h2>Datos Iniciales</h2>
      <div className="form-row">
        <div className="form-group">
          <label>Nombre:</label>
          <input
            type="text"
            placeholder="Tu nombre"
            value={formData.firstName}
            onChange={(e) => handleInputChange('firstName', e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Apellido:</label>
          <input
            type="text"
            placeholder="Tu apellido"
            value={formData.lastName}
            onChange={(e) => handleInputChange('lastName', e.target.value)}
            required
          />
        </div>
      </div>
      
      <div className="form-group">
        <label>Nombre de Usuario:</label>
        <input
          type="text"
          placeholder="Nombre de usuario único"
          value={formData.username}
          onChange={(e) => handleInputChange('username', e.target.value)}
          required
        />
      </div>
      
      <div className="form-group">
        <label>Correo Electrónico:</label>
        <input
          type="email"
          placeholder="tu@email.com"
          value={formData.email}
          onChange={(e) => handleInputChange('email', e.target.value)}
          required
        />
      </div>
      
      <div className="form-group">
        <label>Teléfono:</label>
        <input
          type="tel"
          placeholder="Número de teléfono"
          value={formData.phone}
          onChange={(e) => handleInputChange('phone', e.target.value)}
          required
        />
      </div>
      
      <div className="form-row">
        <div className="form-group">
          <label>Contraseña:</label>
          <input
            type="password"
            placeholder="Mínimo 6 caracteres"
            value={formData.password}
            onChange={(e) => handleInputChange('password', e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Confirmar Contraseña:</label>
          <input
            type="password"
            placeholder="Repite tu contraseña"
            value={formData.confirmPassword}
            onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
            required
          />
        </div>
      </div>
    </div>
  );

  // Componente Step 2 - Selección de Plan
  const Step2 = () => (
    <div className="step-content">
      <h2>Selecciona tu Plan</h2>
      <div className="plan-options">
        <div 
          className={`plan-card ${formData.planType === 'monthly' ? 'selected' : ''}`}
          onClick={() => handleInputChange('planType', 'monthly')}
        >
          <div className="plan-header">
            <h3>Plan Mensual</h3>
            <div className="price">$29.99<span>/mes</span></div>
          </div>
          <ul className="plan-features">
            <li>Acceso completo a la plataforma</li>
            <li>Soporte técnico 24/7</li>
            <li>Actualizaciones incluidas</li>
            <li>Facturación mensual</li>
          </ul>
        </div>
        
        <div 
          className={`plan-card ${formData.planType === 'annual' ? 'selected' : ''}`}
          onClick={() => handleInputChange('planType', 'annual')}
        >
          <div className="plan-header">
            <h3>Plan Anual</h3>
            <div className="price">$299.99<span>/año</span></div>
            <div className="discount">¡Ahorra 2 meses!</div>
          </div>
          <ul className="plan-features">
            <li>Acceso completo a la plataforma</li>
            <li>Soporte técnico prioritario</li>
            <li>Actualizaciones incluidas</li>
            <li>Descuento por pago anual</li>
            <li>Funciones premium exclusivas</li>
          </ul>
        </div>
      </div>
    </div>
  );

  // Componente Step 3 - Datos de Tarjeta
  const Step3 = () => (
    <div className="step-content">
      <h2>Información de Pago</h2>
      <div className="payment-summary">
        <h3>Resumen del Plan:</h3>
        <p>{formData.planType === 'monthly' ? 'Plan Mensual - $29.99/mes' : 'Plan Anual - $299.99/año'}</p>
      </div>
      
      <div className="form-group">
        <label>Número de Tarjeta:</label>
        <input
          type="text"
          placeholder="1234 5678 9012 3456"
          value={formData.cardNumber}
          onChange={(e) => handleInputChange('cardNumber', formatCardNumber(e.target.value))}
          maxLength="19"
          required
        />
      </div>
      
      <div className="form-group">
        <label>Nombre en la Tarjeta:</label>
        <input
          type="text"
          placeholder="Nombre como aparece en la tarjeta"
          value={formData.cardName}
          onChange={(e) => handleInputChange('cardName', e.target.value)}
          required
        />
      </div>
      
      <div className="form-row">
        <div className="form-group">
          <label>Fecha de Vencimiento:</label>
          <input
            type="text"
            placeholder="MM/AA"
            value={formData.expiryDate}
            onChange={(e) => handleInputChange('expiryDate', formatExpiryDate(e.target.value))}
            maxLength="5"
            required
          />
        </div>
        <div className="form-group">
          <label>CVV:</label>
          <input
            type="text"
            placeholder="123"
            value={formData.cvv}
            onChange={(e) => handleInputChange('cvv', e.target.value.replace(/\D/g, ''))}
            maxLength="3"
            required
          />
        </div>
      </div>
      
      <div className="form-group">
        <label>Dirección de Facturación:</label>
        <input
          type="text"
          placeholder="Calle y número"
          value={formData.billingAddress}
          onChange={(e) => handleInputChange('billingAddress', e.target.value)}
          required
        />
      </div>
      
      <div className="form-row">
        <div className="form-group">
          <label>Ciudad:</label>
          <input
            type="text"
            placeholder="Ciudad"
            value={formData.city}
            onChange={(e) => handleInputChange('city', e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Código Postal:</label>
          <input
            type="text"
            placeholder="Código postal"
            value={formData.postalCode}
            onChange={(e) => handleInputChange('postalCode', e.target.value)}
            required
          />
        </div>
      </div>
    </div>
  );

  return (
    <div className="signup-container">
      <div className="user-icon">
        <img src="cloudfit_l.png" alt="Icono Usuario" />
      </div>
      
      <div className="progress-bar">
        <div className="progress-steps">
          <div className={`step ${currentStep >= 1 ? 'active' : ''}`}>1</div>
          <div className="step-line"></div>
          <div className={`step ${currentStep >= 2 ? 'active' : ''}`}>2</div>
          <div className="step-line"></div>
          <div className={`step ${currentStep >= 3 ? 'active' : ''}`}>3</div>
        </div>
      </div>

      <div>
        {currentStep === 1 && <Step1 />}
        {currentStep === 2 && <Step2 />}
        {currentStep === 3 && <Step3 />}

        <div className="form-navigation">
          {currentStep > 1 && (
            <button type="button" onClick={prevStep} className="btn-secondary">
              Anterior
            </button>
          )}
          
          {currentStep < 3 ? (
            <button type="button" onClick={nextStep} className="btn-primary">
              Siguiente
            </button>
          ) : (
            <button type="button" onClick={handleSubmit} className="btn-primary">
              Completar Registro
            </button>
          )}
        </div>
      </div>

      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">{success}</p>}
      
      <p>¿Ya tienes una cuenta? <a href="/">Iniciar Sesión</a></p>

      <style jsx>{`
        .signup-container {
          max-width: 600px;
          margin: 0 auto;
          padding: 20px;
          font-family: Arial, sans-serif;
        }

        .user-icon {
          text-align: center;
          margin-bottom: 30px;
        }

        .user-icon img {
          width: 80px;
          height: 80px;
        }

        .progress-bar {
          margin-bottom: 30px;
        }

        .progress-steps {
          display: flex;
          align-items: center;
          justify-content: center;
          margin-bottom: 20px;
        }

        .step {
          width: 40px;
          height: 40px;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          background-color: #e0e0e0;
          color: #666;
          font-weight: bold;
          transition: all 0.3s ease;
        }

        .step.active {
          background-color: #007bff;
          color: white;
        }

        .step-line {
          width: 60px;
          height: 2px;
          background-color: #e0e0e0;
        }

        .step-content {
          margin-bottom: 30px;
        }

        .step-content h2 {
          text-align: center;
          margin-bottom: 30px;
          color: #333;
        }

        .form-group {
          margin-bottom: 20px;
          flex: 1;
        }

        .form-row {
          display: flex;
          gap: 15px;
        }

        .form-group label {
          display: block;
          margin-bottom: 5px;
          font-weight: bold;
          color: #333;
        }

        .form-group input {
          width: 100%;
          padding: 12px;
          border: 2px solid #ddd;
          border-radius: 8px;
          font-size: 16px;
          transition: border-color 0.3s ease;
          box-sizing: border-box;
        }

        .form-group input:focus {
          outline: none;
          border-color: #007bff;
        }

        .plan-options {
          display: flex;
          gap: 20px;
          margin-bottom: 30px;
        }

        .plan-card {
          flex: 1;
          border: 2px solid #ddd;
          border-radius: 12px;
          padding: 25px;
          cursor: pointer;
          transition: all 0.3s ease;
          background: white;
        }

        .plan-card:hover {
          border-color: #007bff;
          transform: translateY(-2px);
          box-shadow: 0 4px 12px rgba(0,123,255,0.15);
        }

        .plan-card.selected {
          border-color: #007bff;
          background-color: #f8f9ff;
        }

        .plan-header {
          text-align: center;
          margin-bottom: 20px;
        }

        .plan-header h3 {
          margin: 0 0 10px 0;
          color: #333;
        }

        .price {
          font-size: 24px;
          font-weight: bold;
          color: #007bff;
        }

        .price span {
          font-size: 16px;
          color: #666;
        }

        .discount {
          background-color: #28a745;
          color: white;
          padding: 4px 8px;
          border-radius: 12px;
          font-size: 12px;
          margin-top: 8px;
          display: inline-block;
        }

        .plan-features {
          list-style: none;
          padding: 0;
          margin: 0;
        }

        .plan-features li {
          padding: 8px 0;
          border-bottom: 1px solid #eee;
          color: #666;
        }

        .plan-features li:last-child {
          border-bottom: none;
        }

        .payment-summary {
          background-color: #f8f9fa;
          padding: 20px;
          border-radius: 8px;
          margin-bottom: 25px;
          text-align: center;
        }

        .payment-summary h3 {
          margin: 0 0 10px 0;
          color: #333;
        }

        .payment-summary p {
          margin: 0;
          font-size: 18px;
          color: #007bff;
          font-weight: bold;
        }

        .form-navigation {
          display: flex;
          justify-content: space-between;
          gap: 15px;
          margin-top: 30px;
        }

        .btn-primary, .btn-secondary {
          padding: 12px 30px;
          border: none;
          border-radius: 8px;
          font-size: 16px;
          font-weight: bold;
          cursor: pointer;
          transition: all 0.3s ease;
          flex: 1;
        }

        .btn-primary {
          background-color: #007bff;
          color: white;
        }

        .btn-primary:hover {
          background-color: #0056b3;
        }

        .btn-secondary {
          background-color: #6c757d;
          color: white;
        }

        .btn-secondary:hover {
          background-color: #545b62;
        }

        .error-message {
          color: #dc3545;
          text-align: center;
          margin-top: 15px;
          padding: 10px;
          background-color: #f8d7da;
          border-radius: 5px;
        }

        .success-message {
          color: #155724;
          text-align: center;
          margin-top: 15px;
          padding: 10px;
          background-color: #d4edda;
          border-radius: 5px;
        }

        p {
          text-align: center;
          margin-top: 20px;
        }

        a {
          color: #007bff;
          text-decoration: none;
        }

        a:hover {
          text-decoration: underline;
        }

        @media (max-width: 768px) {
          .plan-options {
            flex-direction: column;
          }
          
          .form-row {
            flex-direction: column;
          }
          
          .form-navigation {
            flex-direction: column;
          }
        }
      `}</style>
    </div>
  );
};

export default Signup;
