import React, { useState, useEffect } from 'react';

const EmployeesManagement = () => {
  const [employees, setEmployees] = useState([]);
  const [filteredEmployees, setFilteredEmployees] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [positionFilter, setPositionFilter] = useState('all');
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    position: 'Barbero',
    phone: ''
  });

  useEffect(() => {
    fetchEmployees();
  }, []);

  useEffect(() => {
    filterEmployees();
  }, [employees, searchTerm, statusFilter, positionFilter]);

  const fetchEmployees = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/empleados/all', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      const data = await response.json();
      
      if (data.success) {
        setEmployees(data.employees);
      } else {
        console.error('Error:', data.message);
      }
    } catch (error) {
      console.error('Error fetching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  const filterEmployees = () => {
    let filtered = employees;

    // Filtro de b√∫squeda
    if (searchTerm) {
      filtered = filtered.filter(employee =>
        employee.username?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.employeeId?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.phone?.includes(searchTerm)
      );
    }

    // Filtro de estado
    if (statusFilter !== 'all') {
      filtered = filtered.filter(employee => employee.status === statusFilter);
    }

    // Filtro de puesto
    if (positionFilter !== 'all') {
      filtered = filtered.filter(employee => employee.position === positionFilter);
    }

    setFilteredEmployees(filtered);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      
      if (editingEmployee) {
        // Actualizar empleado existente
        const response = await fetch(`/api/empleados/${editingEmployee.employeeId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(formData)
        });
        
        const data = await response.json();
        
        if (data.success) {
          await fetchEmployees(); // Recargar lista
          alert('Empleado actualizado exitosamente');
        } else {
          alert(data.message || 'Error al actualizar empleado');
        }
      } else {
        // Crear nuevo empleado
        const response = await fetch('/api/empleados/create', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify(formData)
        });
        
        const data = await response.json();
        
        if (data.success) {
          await fetchEmployees(); // Recargar lista
          alert(`Empleado creado exitosamente!`);
        } else {
          alert(data.message || 'Error al crear empleado');
        }
      }
      
      resetForm();
      setShowAddModal(false);
      setEditingEmployee(null);
    } catch (error) {
      console.error('Error saving employee:', error);
      alert('Error al guardar empleado');
    }
  };

  const resetForm = () => {
    setFormData({
      username: '',
      email: '',
      password: '',
      position: 'Barbero',
      phone: ''
    });
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    setFormData({
      username: employee.username || '',
      email: employee.email || '',
      password: '', // No mostrar contrase√±a actual
      position: employee.position || 'Barbero',
      phone: employee.phone || ''
    });
    setShowAddModal(true);
  };

  const handleStatusChange = async (employeeId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/empleados/${employeeId}/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ status: newStatus })
      });
      
      const data = await response.json();
      
      if (data.success) {
        await fetchEmployees(); // Recargar lista
        alert(`Empleado ${newStatus === 'active' ? 'activado' : 'desactivado'} exitosamente`);
      } else {
        alert(data.message || 'Error al cambiar estado');
      }
    } catch (error) {
      console.error('Error updating employee status:', error);
      alert('Error al cambiar estado del empleado');
    }
  };

  const handleDelete = async (employeeId) => {
    if (window.confirm('¬øEst√°s seguro de que quieres eliminar este empleado? Esta acci√≥n no se puede deshacer.')) {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`/api/empleados/${employeeId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        
        const data = await response.json();
        
        if (data.success) {
          await fetchEmployees(); // Recargar lista
          alert('Empleado eliminado exitosamente');
        } else {
          alert(data.message || 'Error al eliminar empleado');
        }
      } catch (error) {
        console.error('Error deleting employee:', error);
        alert('Error al eliminar empleado');
      }
    }
  };

  const openAddModal = () => {
    resetForm();
    setEditingEmployee(null);
    setShowAddModal(true);
  };

  const closeModal = () => {
    setShowAddModal(false);
    setEditingEmployee(null);
    resetForm();
  };

  const getPositionIcon = (position) => {
    const icons = {
      'Barbero': '‚úÇÔ∏è',
      'Estilista': 'üíá‚Äç‚ôÄÔ∏è',
      'Coach': 'üèãÔ∏è‚Äç‚ôÇÔ∏è'
    };
    return icons[position] || 'üë®‚Äçüíº';
  };

  return (
    <div className="employees-management">
      <div className="section-header">
        <h2>Gesti√≥n de Empleados</h2>
        <div className="users-stats">
          <div className="stat-card mini">
            <span className="stat-number">{employees.length}</span>
            <span className="stat-label">Total</span>
          </div>
          <div className="stat-card mini">
            <span className="stat-number">{employees.filter(emp => emp.status === 'active').length}</span>
            <span className="stat-label">Activos</span>
          </div>
          <div className="stat-card mini">
            <span className="stat-number">{employees.filter(emp => emp.status === 'inactive').length}</span>
            <span className="stat-label">Inactivos</span>
          </div>
        </div>
      </div>

      <div className="users-controls">
        <div className="search-section">
          <div className="search-bar">
            <span className="search-icon">üîç</span>
            <input
              type="text"
              placeholder="Buscar empleado por username, email, ID o tel√©fono..."
              value={searchTerm}
              onChange={handleSearchChange}
            />
          </div>
        </div>
        
        <div className="filter-buttons">
          <button 
            className={`filter-btn ${statusFilter === 'all' ? 'active' : ''}`}
            onClick={() => setStatusFilter('all')}
          >
            Todos
          </button>
          <button 
            className={`filter-btn ${statusFilter === 'active' ? 'active' : ''}`}
            onClick={() => setStatusFilter('active')}
          >
            Activos
          </button>
          <button 
            className={`filter-btn ${statusFilter === 'inactive' ? 'active' : ''}`}
            onClick={() => setStatusFilter('inactive')}
          >
            Inactivos
          </button>
          <button 
            className={`filter-btn ${positionFilter === 'Barbero' ? 'active' : ''}`}
            onClick={() => setPositionFilter(positionFilter === 'Barbero' ? 'all' : 'Barbero')}
          >
            Barberos
          </button>
          <button 
            className={`filter-btn ${positionFilter === 'Estilista' ? 'active' : ''}`}
            onClick={() => setPositionFilter(positionFilter === 'Estilista' ? 'all' : 'Estilista')}
          >
            Estilistas
          </button>
          <button 
            className={`filter-btn ${positionFilter === 'Coach' ? 'active' : ''}`}
            onClick={() => setPositionFilter(positionFilter === 'Coach' ? 'all' : 'Coach')}
          >
            Coaches
          </button>
        </div>
      </div>

      <div className="users-controls">
        <button className="btn-primary" onClick={openAddModal}>
          + Nuevo Empleado
        </button>
      </div>

      {loading ? (
        <div className="loading">
          <div className="spinner"></div>
          <p>Cargando empleados...</p>
        </div>
      ) : (
        <div className="users-grid">
          {filteredEmployees.length === 0 ? (
            <div className="no-results">
              <p>No se encontraron empleados</p>
            </div>
          ) : (
            filteredEmployees.map(employee => (
              <div key={employee.employeeId} className="user-card">
                <div className="user-card-header">
                  <div className="user-avatar-section">
                    <div className="user-avatar-large">
                      {getPositionIcon(employee.position)}
                    </div>
                    <div className="user-info-primary">
                      <h4 className="user-full-name">@{employee.username}</h4>
                      <p className="user-id">ID: {employee.employeeId}</p>
                    </div>
                  </div>
                  <div className="user-status-section">
                    <span 
                      className="status-dot"
                      style={{ backgroundColor: employee.status === 'active' ? '#10b981' : '#ef4444' }}
                    ></span>
                    <span className="status-text">{employee.status === 'active' ? 'Activo' : 'Inactivo'}</span>
                  </div>
                </div>

                <div className="user-card-body">
                  <div className="user-detail-row">
                    <span className="detail-icon">üìß</span>
                    <span className="detail-text">{employee.email}</span>
                  </div>
                  <div className="user-detail-row">
                    <span className="detail-icon">üì±</span>
                    <span className="detail-text">{employee.phone}</span>
                  </div>
                  <div className="user-detail-row">
                    <span className="detail-icon">üíº</span>
                    <span className="detail-text">{employee.position}</span>
                  </div>
                </div>

                <div className="user-card-footer">
                  <button
                    className="action-btn view-btn"
                    onClick={() => handleEdit(employee)}
                    title="Editar"
                  >
                    <span>‚úèÔ∏è</span>
                    Editar
                  </button>
                  
                  {employee.status === 'active' ? (
                    <button
                      className="action-btn suspend-btn"
                      onClick={() => handleStatusChange(employee.employeeId, 'inactive')}
                      title="Desactivar"
                    >
                      <span>‚è∏Ô∏è</span>
                      Suspender
                    </button>
                  ) : (
                    <button
                      className="action-btn activate-btn"
                      onClick={() => handleStatusChange(employee.employeeId, 'active')}
                      title="Activar"
                    >
                      <span>‚ñ∂Ô∏è</span>
                      Activar
                    </button>
                  )}
                  
                  <button
                    className="action-btn suspend-btn"
                    onClick={() => handleDelete(employee.employeeId)}
                    title="Eliminar"
                  >
                    <span>üóëÔ∏è</span>
                    Eliminar
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      )}

      {/* Modal para agregar/editar empleado */}
      {showAddModal && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>{editingEmployee ? 'Editar Empleado' : 'Nuevo Empleado'}</h3>
              <button className="close-btn" onClick={closeModal}>‚úï</button>
            </div>
            
            <div className="modal-body">
              <div className="employee-form">
                <div className="form-row">
                  <div className="form-group">
                    <label>Username *</label>
                    <input
                      type="text"
                      name="username"
                      value={formData.username}
                      onChange={handleInputChange}
                      required
                      placeholder="usuario123"
                    />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Email *</label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      required
                      placeholder="usuario@cloudfit.com"
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>Contrase√±a {editingEmployee ? '(opcional)' : '*'}</label>
                    <input
                      type="password"
                      name="password"
                      value={formData.password}
                      onChange={handleInputChange}
                      required={!editingEmployee}
                      placeholder={editingEmployee ? "Nueva contrase√±a (opcional)" : "Contrase√±a"}
                      minLength="6"
                    />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Tel√©fono *</label>
                    <input
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      required
                      placeholder="5551234567"
                      pattern="[0-9]{10}"
                      title="Debe tener exactamente 10 d√≠gitos"
                      maxLength="10"
                    />
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Puesto *</label>
                    <select
                      name="position"
                      value={formData.position}
                      onChange={handleInputChange}
                      required
                      style={{
                        width: '100%',
                        padding: '8px',
                        border: '1px solid #ddd',
                        borderRadius: '6px',
                        backgroundColor: 'white',
                        fontSize: '14px'
                      }}
                    >
                      <option value="Barbero">‚úÇÔ∏è Barbero</option>
                      <option value="Estilista">üíá‚Äç‚ôÄÔ∏è Estilista</option>
                      <option value="Coach">üèãÔ∏è‚Äç‚ôÇÔ∏è Coach</option>
                    </select>
                  </div>
                  
                  <div className="form-group">
                    {/* Campo vac√≠o para mantener el layout */}
                  </div>
                </div>

                {!editingEmployee && (
                  <div className="info-note">
                    <p><strong>Informaci√≥n autom√°tica:</strong></p>
                    <ul>
                      <li>üìã Se generar√° autom√°ticamente un ID √∫nico de empleado (70XXXX)</li>
                      <li>üè∑Ô∏è El rol ser√°: empleado</li>
                    </ul>
                  </div>
                )}
              </div>
            </div>

            <div className="modal-footer">
              <button type="button" className="btn-secondary" onClick={closeModal}>
                Cancelar
              </button>
              <button type="submit" className="btn-primary" onClick={handleSubmit}>
                {editingEmployee ? 'Actualizar' : 'Crear'} Empleado
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default EmployeesManagement;
