import React, { useState } from 'react';
import './rutina.css';

const RoutineManagement = () => {
  const [selectedDay, setSelectedDay] = useState('lunes');
  const [showMachineSelector, setShowMachineSelector] = useState(false);

  const daysOfWeek = [
    'lunes', 'martes', 'mi√©rcoles', 'jueves', 'viernes', 's√°bado', 'domingo'
  ];

  const availableMachines = [
    { id: 1, name: 'Cinta Caminadora', type: 'cardio', muscle_group: 'cardiovascular' },
    { id: 2, name: 'El√≠ptica', type: 'cardio', muscle_group: 'cardiovascular' },
    { id: 3, name: 'Bicicleta Est√°tica', type: 'cardio', muscle_group: 'cardiovascular' },
    { id: 4, name: 'Press de Pecho', type: 'fuerza', muscle_group: 'pecho' },
    { id: 5, name: 'Press de Piernas', type: 'fuerza', muscle_group: 'piernas' },
    { id: 6, name: 'Polea Alta', type: 'fuerza', muscle_group: 'espalda' },
    { id: 7, name: 'Banco Inclinado', type: 'fuerza', muscle_group: 'pecho' },
    { id: 8, name: 'Curl de B√≠ceps', type: 'fuerza', muscle_group: 'brazos' },
    { id: 9, name: 'Extensi√≥n de Tr√≠ceps', type: 'fuerza', muscle_group: 'brazos' },
    { id: 10, name: 'Sentadillas con Barra', type: 'peso_libre', muscle_group: 'piernas' },
    { id: 11, name: 'Peso Muerto', type: 'peso_libre', muscle_group: 'espalda' },
    { id: 12, name: 'Mancuernas', type: 'peso_libre', muscle_group: 'brazos' }
  ];

  const [weeklyRoutine, setWeeklyRoutine] = useState({
    lunes: [],
    martes: [],
    mi√©rcoles: [],
    jueves: [],
    viernes: [],
    s√°bado: [],
    domingo: []
  });

  const addExerciseToDay = (machine) => {
    const newExercise = {
      id: Date.now(),
      machine: machine,
      sets: 3,
      reps: 12,
      weight: 20,
      rest: 60
    };

    setWeeklyRoutine(prev => ({
      ...prev,
      [selectedDay]: [...prev[selectedDay], newExercise]
    }));
    setShowMachineSelector(false);
  };

  const removeExercise = (exerciseId) => {
    setWeeklyRoutine(prev => ({
      ...prev,
      [selectedDay]: prev[selectedDay].filter(ex => ex.id !== exerciseId)
    }));
  };

  const updateExercise = (exerciseId, field, value) => {
    setWeeklyRoutine(prev => ({
      ...prev,
      [selectedDay]: prev[selectedDay].map(ex => 
        ex.id === exerciseId ? { ...ex, [field]: value } : ex
      )
    }));
  };

  const getMachineIcon = (type) => {
    switch (type) {
      case 'cardio': return 'üèÉ';
      case 'fuerza': return 'üèãÔ∏è';
      case 'peso_libre': return 'üí™';
      default: return '‚ö°';
    }
  };

  return (
    <div className="routine-management">
      <div className="routine-header">
        <h2>üí™ Gesti√≥n de Rutina Semanal</h2>
        <button 
          className="add-exercise-btn"
          onClick={() => setShowMachineSelector(true)}
        >
          + Agregar Ejercicio
        </button>
      </div>

      <div className="week-navigator">
        {daysOfWeek.map(day => (
          <button
            key={day}
            className={`day-btn ${selectedDay === day ? 'active' : ''}`}
            onClick={() => setSelectedDay(day)}
          >
            {day.charAt(0).toUpperCase() + day.slice(1)}
          </button>
        ))}
      </div>

      <div className="routine-content">
        <div className="day-routine">
          <h3>Rutina del {selectedDay.charAt(0).toUpperCase() + selectedDay.slice(1)}</h3>
          
          {weeklyRoutine[selectedDay].length === 0 ? (
            <div className="empty-routine">
              <p>No hay ejercicios programados para este d√≠a</p>
              <button 
                className="add-first-exercise"
                onClick={() => setShowMachineSelector(true)}
              >
                Agregar primer ejercicio
              </button>
            </div>
          ) : (
            <div className="exercises-list">
              {weeklyRoutine[selectedDay].map((exercise, index) => (
                <div key={exercise.id} className="exercise-card">
                  <div className="exercise-header">
                    <span className="exercise-number">{index + 1}</span>
                    <span className="machine-icon">
                      {getMachineIcon(exercise.machine.type)}
                    </span>
                    <h4>{exercise.machine.name}</h4>
                    <button 
                      className="remove-btn"
                      onClick={() => removeExercise(exercise.id)}
                    >
                      ‚úï
                    </button>
                  </div>
                  
                  <div className="exercise-params">
                    <div className="param-group">
                      <label>Series:</label>
                      <input
                        type="number"
                        value={exercise.sets}
                        onChange={(e) => updateExercise(exercise.id, 'sets', parseInt(e.target.value))}
                        min="1"
                        max="10"
                      />
                    </div>
                    
                    <div className="param-group">
                      <label>Repeticiones:</label>
                      <input
                        type="number"
                        value={exercise.reps}
                        onChange={(e) => updateExercise(exercise.id, 'reps', parseInt(e.target.value))}
                        min="1"
                        max="50"
                      />
                    </div>
                    
                    <div className="param-group">
                      <label>Peso (kg):</label>
                      <input
                        type="number"
                        value={exercise.weight}
                        onChange={(e) => updateExercise(exercise.id, 'weight', parseInt(e.target.value))}
                        min="0"
                        max="500"
                      />
                    </div>
                    
                    <div className="param-group">
                      <label>Descanso (seg):</label>
                      <input
                        type="number"
                        value={exercise.rest}
                        onChange={(e) => updateExercise(exercise.id, 'rest', parseInt(e.target.value))}
                        min="30"
                        max="300"
                        step="30"
                      />
                    </div>
                  </div>
                  
                  <div className="muscle-group">
                    <span>Grupo muscular: {exercise.machine.muscle_group}</span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="routine-summary">
          <h4>Resumen Semanal</h4>
          <div className="weekly-stats">
            {daysOfWeek.map(day => (
              <div key={day} className="day-stat">
                <span className="day-name">{day.slice(0, 3)}</span>
                <span className="exercise-count">
                  {weeklyRoutine[day].length} ejercicios
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {showMachineSelector && (
        <div className="machine-selector-overlay">
          <div className="machine-selector">
            <div className="selector-header">
              <h3>Seleccionar M√°quina/Ejercicio</h3>
              <button 
                className="close-btn"
                onClick={() => setShowMachineSelector(false)}
              >
                ‚úï
              </button>
            </div>
            
            <div className="machines-grid">
              {availableMachines.map(machine => (
                <div
                  key={machine.id}
                  className="machine-option"
                  onClick={() => addExerciseToDay(machine)}
                >
                  <span className="machine-icon">
                    {getMachineIcon(machine.type)}
                  </span>
                  <h4>{machine.name}</h4>
                  <span className="muscle-group">{machine.muscle_group}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RoutineManagement;
