import React, { useState } from 'react';
import './AppointmentBooking.css';

const AppointmentBooking = () => {
  const [selectedService, setSelectedService] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTime, setSelectedTime] = useState('');
  const [selectedEmployee, setSelectedEmployee] = useState('');
  const [appointments, setAppointments] = useState([]);

  const services = [
    { id: 'barbero', name: 'Servicio de Barbería', icon: '✂️', duration: 30 },
    { id: 'estilista', name: 'Servicio de Estilista', icon: '💇', duration: 45 },
    { id: 'coach', name: 'Entrenamiento Personal', icon: '🏋️', duration: 60 }
  ];

  const employees = {
    barbero: [
      { id: 1, name: 'Carlos Martínez', specialty: 'Cortes clásicos', rating: 4.8 },
      { id: 2, name: 'Roberto Díaz', specialty: 'Barba y bigote', rating: 4.9 }
    ],
    estilista: [
      { id: 3, name: 'Ana García', specialty: 'Cortes modernos', rating: 4.7 },
      { id: 4, name: 'María López', specialty: 'Coloración', rating: 4.9 },
      { id: 5, name: 'Sofia Ruiz', specialty: 'Peinados especiales', rating: 4.8 }
    ],
    coach: [
      { id: 6, name: 'Miguel Torres', specialty: 'Fuerza y resistencia', rating: 4.9 },
      { id: 7, name: 'Laura Jiménez', specialty: 'Cardio y pérdida de peso', rating: 4.8 },
      { id: 8, name: 'David Morales', specialty: 'Rehabilitación', rating: 4.7 }
    ]
  };

  const timeSlots = [
    '08:00', '08:30', '09:00', '09:30', '10:00', '10:30',
    '11:00', '11:30', '12:00', '12:30', '13:00', '13:30',
    '14:00', '14:30', '15:00', '15:30', '16:00', '16:30',
    '17:00', '17:30', '18:00', '18:30', '19:00', '19:30'
  ];

  // Generar próximos 14 días
  const getAvailableDates = () => {
    const dates = [];
    const today = new Date();
    for (let i = 0; i < 14; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      dates.push(date.toISOString().split('T')[0]);
    }
    return dates;
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('es-ES', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const isTimeSlotAvailable = (time) => {
    if (!selectedDate || !selectedEmployee) return true;
    
    return !appointments.some(appointment => 
      appointment.date === selectedDate &&
      appointment.time === time &&
      appointment.employee.id === parseInt(selectedEmployee)
    );
  };

  const handleBookAppointment = () => {
    if (!selectedService || !selectedDate || !selectedTime || !selectedEmployee) {
      alert('Por favor, completa todos los campos');
      return;
    }

    const service = services.find(s => s.id === selectedService);
    const employee = employees[selectedService].find(e => e.id === parseInt(selectedEmployee));

    const newAppointment = {
      id: Date.now(),
      service: service,
      employee: employee,
      date: selectedDate,
      time: selectedTime,
      status: 'confirmada'
    };

    setAppointments([...appointments, newAppointment]);
    
    // Limpiar formulario
    setSelectedService('');
    setSelectedDate('');
    setSelectedTime('');
    setSelectedEmployee('');

    alert('¡Cita agendada exitosamente!');
  };

  const cancelAppointment = (appointmentId) => {
    setAppointments(appointments.filter(app => app.id !== appointmentId));
    alert('Cita cancelada exitosamente');
  };

  return (
    <div className="appointment-booking">
      <div className="booking-header">
        <h2>📅 Agendar Citas</h2>
      </div>

      <div className="booking-content">
        <div className="booking-form">
          <h3>Nueva Cita</h3>
          
          {/* Selección de servicio */}
          <div className="form-section">
            <label>Selecciona el servicio:</label>
            <div className="service-options">
              {services.map(service => (
                <div
                  key={service.id}
                  className={`service-card ${selectedService === service.id ? 'selected' : ''}`}
                  onClick={() => {
                    setSelectedService(service.id);
                    setSelectedEmployee(''); // Reset employee selection
                  }}
                >
                  <span className="service-icon">{service.icon}</span>
                  <h4>{service.name}</h4>
                  <span className="service-duration">{service.duration} min</span>
                </div>
              ))}
            </div>
          </div>

          {/* Selección de empleado */}
          {selectedService && (
            <div className="form-section">
              <label>Selecciona el profesional:</label>
              <div className="employee-options">
                {employees[selectedService].map(employee => (
                  <div
                    key={employee.id}
                    className={`employee-card ${selectedEmployee === employee.id.toString() ? 'selected' : ''}`}
                    onClick={() => setSelectedEmployee(employee.id.toString())}
                  >
                    <div className="employee-info">
                      <h4>{employee.name}</h4>
                      <span className="specialty">{employee.specialty}</span>
                      <div className="rating">
                        ⭐ {employee.rating}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Selección de fecha */}
          {selectedEmployee && (
            <div className="form-section">
              <label>Selecciona la fecha:</label>
              <div className="date-options">
                {getAvailableDates().map(date => (
                  <button
                    key={date}
                    className={`date-btn ${selectedDate === date ? 'selected' : ''}`}
                    onClick={() => {
                      setSelectedDate(date);
                      setSelectedTime(''); // Reset time selection
                    }}
                  >
                    {formatDate(date)}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Selección de hora */}
          {selectedDate && (
            <div className="form-section">
              <label>Selecciona la hora:</label>
              <div className="time-options">
                {timeSlots.map(time => (
                  <button
                    key={time}
                    className={`time-btn ${selectedTime === time ? 'selected' : ''} ${!isTimeSlotAvailable(time) ? 'unavailable' : ''}`}
                    onClick={() => setSelectedTime(time)}
                    disabled={!isTimeSlotAvailable(time)}
                  >
                    {time}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Botón de confirmar */}
          {selectedService && selectedEmployee && selectedDate && selectedTime && (
            <div className="form-section">
              <button className="confirm-btn" onClick={handleBookAppointment}>
                Confirmar Cita
              </button>
            </div>
          )}
        </div>

        {/* Lista de citas */}
        <div className="appointments-list">
          <h3>Mis Citas</h3>
          {appointments.length === 0 ? (
            <div className="no-appointments">
              <p>No tienes citas agendadas</p>
            </div>
          ) : (
            <div className="appointments">
              {appointments.map(appointment => (
                <div key={appointment.id} className="appointment-card">
                  <div className="appointment-header">
                    <span className="service-icon">{appointment.service.icon}</span>
                    <div className="appointment-info">
                      <h4>{appointment.service.name}</h4>
                      <span className="employee-name">con {appointment.employee.name}</span>
                    </div>
                    <button 
                      className="cancel-btn"
                      onClick={() => cancelAppointment(appointment.id)}
                    >
                      ✕
                    </button>
                  </div>
                  
                  <div className="appointment-details">
                    <div className="detail-item">
                      <span className="detail-label">Fecha:</span>
                      <span className="detail-value">{formatDate(appointment.date)}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Hora:</span>
                      <span className="detail-value">{appointment.time}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Duración:</span>
                      <span className="detail-value">{appointment.service.duration} min</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Estado:</span>
                      <span className={`status ${appointment.status}`}>{appointment.status}</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AppointmentBooking;
