import React, { useState } from 'react';
import './ProfileManagement.css';

const ProfileManagement = ({ user }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    username: user?.username || '',
    email: user?.email || '',
    firstName: user?.firstName || '',
    lastName: user?.lastName || '',
    phone: user?.phone || '',
    dateOfBirth: user?.dateOfBirth || '',
    gender: user?.gender || '',
    height: user?.height || '',
    weight: user?.weight || '',
    fitnessGoal: user?.fitnessGoal || '',
    medicalConditions: user?.medicalConditions || '',
    emergencyContact: user?.emergencyContact || '',
    emergencyPhone: user?.emergencyPhone || '',
    membershipType: user?.membershipType || 'básica'
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Limpiar error específico cuando el usuario empiece a escribir
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.username.trim()) {
      newErrors.username = 'El nombre de usuario es requerido';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'El email es requerido';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'El email no tiene un formato válido';
    }

    if (!formData.firstName.trim()) {
      newErrors.firstName = 'El nombre es requerido';
    }

    if (!formData.lastName.trim()) {
      newErrors.lastName = 'El apellido es requerido';
    }

    if (formData.phone && !/^\d{10}$/.test(formData.phone.replace(/\D/g, ''))) {
      newErrors.phone = 'El teléfono debe tener 10 dígitos';
    }

    if (formData.height && (formData.height < 100 || formData.height > 250)) {
      newErrors.height = 'La altura debe estar entre 100 y 250 cm';
    }

    if (formData.weight && (formData.weight < 30 || formData.weight > 300)) {
      newErrors.weight = 'El peso debe estar entre 30 y 300 kg';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = () => {
    if (validateForm()) {
      // Aquí normalmente enviarías los datos al servidor
      console.log('Datos a guardar:', formData);
      setIsEditing(false);
      alert('Perfil actualizado exitosamente');
    }
  };

  const handleCancel = () => {
    setFormData({
      username: user?.username || '',
      email: user?.email || '',
      firstName: user?.firstName || '',
      lastName: user?.lastName || '',
      phone: user?.phone || '',
      dateOfBirth: user?.dateOfBirth || '',
      gender: user?.gender || '',
      height: user?.height || '',
      weight: user?.weight || '',
      fitnessGoal: user?.fitnessGoal || '',
      medicalConditions: user?.medicalConditions || '',
      emergencyContact: user?.emergencyContact || '',
      emergencyPhone: user?.emergencyPhone || '',
      membershipType: user?.membershipType || 'básica'
    });
    setErrors({});
    setIsEditing(false);
  };

  const calculateBMI = () => {
    if (formData.height && formData.weight) {
      const heightInMeters = formData.height / 100;
      const bmi = formData.weight / (heightInMeters * heightInMeters);
      return bmi.toFixed(1);
    }
    return null;
  };

  const getBMICategory = (bmi) => {
    if (bmi < 18.5) return { category: 'Bajo peso', color: '#3498db' };
    if (bmi < 25) return { category: 'Peso normal', color: '#10B981' };
    if (bmi < 30) return { category: 'Sobrepeso', color: '#F59E0B' };
    return { category: 'Obesidad', color: '#EF4444' };
  };

  const bmi = calculateBMI();
  const bmiInfo = bmi ? getBMICategory(parseFloat(bmi)) : null;

  return (
    <div className="profile-management">
      <div className="profile-header">
        <h2>⚙️ Gestión de Perfil</h2>
        {!isEditing ? (
          <button className="edit-btn" onClick={() => setIsEditing(true)}>
            ✏️ Editar Perfil
          </button>
        ) : (
          <div className="action-buttons">
            <button className="save-btn" onClick={handleSave}>
              ✅ Guardar
            </button>
            <button className="cancel-btn" onClick={handleCancel}>
              ❌ Cancelar
            </button>
          </div>
        )}
      </div>

      <div className="profile-content">
        <div className="profile-sections">
          {/* Información Personal */}
          <div className="profile-section">
            <h3>👤 Información Personal</h3>
            <div className="form-grid">
              <div className="form-group">
                <label>Nombre de Usuario</label>
                <input
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className={errors.username ? 'error' : ''}
                />
                {errors.username && <span className="error-message">{errors.username}</span>}
              </div>

              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className={errors.email ? 'error' : ''}
                />
                {errors.email && <span className="error-message">{errors.email}</span>}
              </div>

              <div className="form-group">
                <label>Nombre</label>
                <input
                  type="text"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className={errors.firstName ? 'error' : ''}
                />
                {errors.firstName && <span className="error-message">{errors.firstName}</span>}
              </div>

              <div className="form-group">
                <label>Apellido</label>
                <input
                  type="text"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className={errors.lastName ? 'error' : ''}
                />
                {errors.lastName && <span className="error-message">{errors.lastName}</span>}
              </div>

              <div className="form-group">
                <label>Teléfono</label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className={errors.phone ? 'error' : ''}
                  placeholder="1234567890"
                />
                {errors.phone && <span className="error-message">{errors.phone}</span>}
              </div>

              <div className="form-group">
                <label>Fecha de Nacimiento</label>
                <input
                  type="date"
                  name="dateOfBirth"
                  value={formData.dateOfBirth}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                />
              </div>

              <div className="form-group">
                <label>Género</label>
                <select
                  name="gender"
                  value={formData.gender}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                >
                  <option value="">Seleccionar</option>
                  <option value="masculino">Masculino</option>
                  <option value="femenino">Femenino</option>
                  <option value="otro">Otro</option>
                </select>
              </div>
            </div>
          </div>

          {/* Información Física */}
          <div className="profile-section">
            <h3>📏 Información Física</h3>
            <div className="form-grid">
              <div className="form-group">
                <label>Altura (cm)</label>
                <input
                  type="number"
                  name="height"
                  value={formData.height}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className={errors.height ? 'error' : ''}
                  min="100"
                  max="250"
                />
                {errors.height && <span className="error-message">{errors.height}</span>}
              </div>

              <div className="form-group">
                <label>Peso (kg)</label>
                <input
                  type="number"
                  name="weight"
                  value={formData.weight}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className={errors.weight ? 'error' : ''}
                  min="30"
                  max="300"
                  step="0.1"
                />
                {errors.weight && <span className="error-message">{errors.weight}</span>}
              </div>

              <div className="form-group">
                <label>Objetivo de Fitness</label>
                <select
                  name="fitnessGoal"
                  value={formData.fitnessGoal}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                >
                  <option value="">Seleccionar</option>
                  <option value="perder_peso">Perder peso</option>
                  <option value="ganar_musculo">Ganar músculo</option>
                  <option value="mantener_forma">Mantener forma</option>
                  <option value="mejorar_resistencia">Mejorar resistencia</option>
                  <option value="rehabilitacion">Rehabilitación</option>
                </select>
              </div>
            </div>

            {bmi && (
              <div className="bmi-info">
                <h4>Índice de Masa Corporal (IMC)</h4>
                <div className="bmi-result">
                  <span className="bmi-value">{bmi}</span>
                  <span 
                    className="bmi-category"
                    style={{ color: bmiInfo.color }}
                  >
                    {bmiInfo.category}
                  </span>
                </div>
              </div>
            )}
          </div>

          {/* Información Médica */}
          <div className="profile-section">
            <h3>🏥 Información Médica</h3>
            <div className="form-group full-width">
              <label>Condiciones Médicas</label>
              <textarea
                name="medicalConditions"
                value={formData.medicalConditions}
                onChange={handleInputChange}
                disabled={!isEditing}
                placeholder="Describe cualquier condición médica relevante..."
                rows="3"
              />
            </div>
          </div>

          {/* Contacto de Emergencia */}
          <div className="profile-section">
            <h3>🚨 Contacto de Emergencia</h3>
            <div className="form-grid">
              <div className="form-group">
                <label>Nombre del Contacto</label>
                <input
                  type="text"
                  name="emergencyContact"
                  value={formData.emergencyContact}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  placeholder="Nombre completo"
                />
              </div>

              <div className="form-group">
                <label>Teléfono de Emergencia</label>
                <input
                  type="tel"
                  name="emergencyPhone"
                  value={formData.emergencyPhone}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  placeholder="1234567890"
                />
              </div>
            </div>
          </div>

          {/* Membresía */}
          <div className="profile-section">
            <h3>💳 Información de Membresía</h3>
            <div className="membership-info">
              <div className="membership-current">
                <span className="membership-label">Membresía Actual:</span>
                <span className={`membership-type ${formData.membershipType}`}>
                  {formData.membershipType.charAt(0).toUpperCase() + formData.membershipType.slice(1)}
                </span>
              </div>
              {!isEditing && (
                <button className="upgrade-btn">
                  Actualizar Membresía
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileManagement;
