import React, { useState, useEffect } from 'react';
import './citas.css';

const AppointmentBooking = () => {
  const [selectedService, setSelectedService] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTime, setSelectedTime] = useState('');
  const [selectedEmployee, setSelectedEmployee] = useState('');
  const [appointments, setAppointments] = useState([]);
  const [employees, setEmployees] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loadingEmployees, setLoadingEmployees] = useState(false);
  const [checkingAvailability, setCheckingAvailability] = useState(false);

  const services = [
    { id: 'barbero', name: 'Servicio de Barber√≠a', icon: '‚úÇÔ∏è', duration: 30 },
    { id: 'estilista', name: 'Servicio de Estilista', icon: 'üíá', duration: 45 },
    { id: 'coach', name: 'Entrenamiento Personal', icon: 'üèãÔ∏è', duration: 60 }
  ];

  const timeSlots = [
    '08:00', '08:30', '09:00', '09:30', '10:00', '10:30',
    '11:00', '11:30', '12:00', '12:30', '13:00', '13:30',
    '14:00', '14:30', '15:00', '15:30', '16:00', '16:30',
    '17:00', '17:30', '18:00', '18:30', '19:00', '19:30'
  ];

  // Cargar datos al iniciar
  useEffect(() => {
    fetchEmployees();
    fetchAppointments();
  }, []);

  // Limpiar mensajes despu√©s de 5 segundos
  useEffect(() => {
    if (success || error) {
      const timer = setTimeout(() => {
        setSuccess('');
        setError('');
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [success, error]);

  const fetchEmployees = async () => {
    try {
      setLoadingEmployees(true);
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/empleados/all', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      const data = await response.json();
      
      if (response.ok && data.success) {
        // Organizar empleados por posici√≥n
        const employeesByPosition = {
          barbero: [],
          estilista: [],
          coach: []
        };

        data.employees.forEach(employee => {
          const position = employee.position.toLowerCase();
          if (employeesByPosition[position]) {
            employeesByPosition[position].push({
              id: employee.employeeId,
              name: employee.username,
              specialty: getSpecialtyByPosition(employee.position),
              rating: generateRating(), // Generar rating aleatorio por ahora
              position: employee.position,
              phone: employee.phone,
              email: employee.email,
              status: employee.status
            });
          }
        });

        setEmployees(employeesByPosition);
      } else {
        setError('Error al cargar empleados');
      }
    } catch (error) {
      console.error('Error fetching employees:', error);
      setError('Error de conexi√≥n al cargar empleados');
    } finally {
      setLoadingEmployees(false);
    }
  };

  const fetchAppointments = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/citas/my-appointments', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      const data = await response.json();
      
      if (response.ok && data.success) {
        setAppointments(data.appointments || []);
      } else {
        console.error('Error al cargar citas:', data.message);
      }
    } catch (error) {
      console.error('Error fetching appointments:', error);
    } finally {
      setLoading(false);
    }
  };

  const checkTimeSlotAvailability = async (date, time, employeeId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(
        `http://localhost:5000/api/citas/check-availability?date=${date}&time=${time}&employeeId=${employeeId}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );
      
      const data = await response.json();
      return response.ok && data.success ? data.available : false;
    } catch (error) {
      console.error('Error checking availability:', error);
      return false;
    }
  };

  const getSpecialtyByPosition = (position) => {
    const specialties = {
      'Barbero': ['Cortes cl√°sicos', 'Barba y bigote', 'Cortes modernos'][Math.floor(Math.random() * 3)],
      'Estilista': ['Cortes modernos', 'Coloraci√≥n', 'Peinados especiales'][Math.floor(Math.random() * 3)],
      'Coach': ['Fuerza y resistencia', 'Cardio y p√©rdida de peso', 'Rehabilitaci√≥n'][Math.floor(Math.random() * 3)]
    };
    return specialties[position] || 'Servicio general';
  };

  const generateRating = () => {
    return (4.5 + Math.random() * 0.5).toFixed(1);
  };

  // Generar pr√≥ximos 14 d√≠as
  const getAvailableDates = () => {
    const dates = [];
    const today = new Date();
    for (let i = 0; i < 14; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      dates.push(date.toISOString().split('T')[0]);
    }
    return dates;
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('es-ES', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const isTimeSlotAvailable = (time) => {
    if (!selectedDate || !selectedEmployee) return true;
    
    // Verificar en citas locales primero
    return !appointments.some(appointment => 
      appointment.date === selectedDate &&
      appointment.time === time &&
      appointment.employee.employeeId === selectedEmployee
    );
  };

  const handleBookAppointment = async () => {
    if (!selectedService || !selectedDate || !selectedTime || !selectedEmployee) {
      setError('Por favor, completa todos los campos');
      return;
    }

    try {
      setLoading(true);
      setError('');
      setSuccess('');

      // Verificar disponibilidad una vez m√°s antes de agendar
      const isAvailable = await checkTimeSlotAvailability(selectedDate, selectedTime, selectedEmployee);
      if (!isAvailable) {
        setError('El horario seleccionado ya no est√° disponible');
        return;
      }

      const service = services.find(s => s.id === selectedService);
      const employee = employees[selectedService].find(e => e.id === selectedEmployee);

      const appointmentData = {
        serviceType: selectedService,
        serviceName: service.name,
        serviceDuration: service.duration,
        employeeId: selectedEmployee,
        date: selectedDate,
        time: selectedTime,
        notes: ''
      };

      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/citas/book', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(appointmentData)
      });

      const data = await response.json();

      if (response.ok && data.success) {
        setSuccess('¬°Cita agendada exitosamente!');
        
        // Recargar citas
        await fetchAppointments();
        
        // Limpiar formulario
        setSelectedService('');
        setSelectedDate('');
        setSelectedTime('');
        setSelectedEmployee('');
      } else {
        setError(data.message || 'Error al agendar la cita');
      }
    } catch (error) {
      console.error('Error booking appointment:', error);
      setError('Error de conexi√≥n al agendar la cita');
    } finally {
      setLoading(false);
    }
  };

  const cancelAppointment = async (appointmentId) => {
    if (!window.confirm('¬øEst√°s seguro de que quieres cancelar esta cita?')) {
      return;
    }

    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/citas/appointment/${appointmentId}/cancel`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const data = await response.json();

      if (response.ok && data.success) {
        setSuccess('Cita cancelada exitosamente');
        await fetchAppointments();
      } else {
        setError(data.message || 'Error al cancelar la cita');
      }
    } catch (error) {
      console.error('Error canceling appointment:', error);
      setError('Error de conexi√≥n al cancelar la cita');
    } finally {
      setLoading(false);
    }
  };

  const getStatusDisplay = (status) => {
    const statusMap = {
      'pending': 'Pendiente',
      'confirmed': 'Confirmada',
      'cancelled': 'Cancelada',
      'completed': 'Completada'
    };
    return statusMap[status] || status;
  };

  const getStatusClass = (status) => {
    const classMap = {
      'pending': 'pending',
      'confirmed': 'confirmed',
      'cancelled': 'cancelled',
      'completed': 'completed'
    };
    return classMap[status] || 'pending';
  };

  return (
    <div className="appointment-booking">
      <div className="booking-header">
        <h2>üìÖ Agendar Citas</h2>
      </div>

      {/* Mostrar mensajes globales */}
      {error && <div className="global-error-message">{error}</div>}
      {success && <div className="global-success-message">{success}</div>}

      <div className="booking-content">
        <div className="booking-form">
          <h3>Nueva Cita</h3>
          
          {/* Selecci√≥n de servicio */}
          <div className="form-section">
            <label>Selecciona el servicio:</label>
            <div className="service-options">
              {services.map(service => (
                <div
                  key={service.id}
                  className={`service-card ${selectedService === service.id ? 'selected' : ''}`}
                  onClick={() => {
                    setSelectedService(service.id);
                    setSelectedEmployee(''); // Reset employee selection
                    setSelectedDate(''); // Reset date selection
                    setSelectedTime(''); // Reset time selection
                  }}
                >
                  <span className="service-icon">{service.icon}</span>
                  <h4>{service.name}</h4>
                  <span className="service-duration">{service.duration} min</span>
                </div>
              ))}
            </div>
          </div>

          {/* Selecci√≥n de empleado */}
          {selectedService && (
            <div className="form-section">
              <label>Selecciona el profesional:</label>
              {loadingEmployees ? (
                <div className="loading-employees">
                  <div className="spinner"></div>
                  <p>Cargando profesionales...</p>
                </div>
              ) : employees[selectedService]?.length === 0 ? (
                <div className="no-employees">
                  <p>No hay profesionales disponibles para este servicio</p>
                </div>
              ) : (
                <div className="employee-options">
                  {employees[selectedService]?.map(employee => (
                    <div
                      key={employee.id}
                      className={`employee-card ${selectedEmployee === employee.id ? 'selected' : ''} ${employee.status !== 'active' ? 'unavailable' : ''}`}
                      onClick={() => {
                        if (employee.status === 'active') {
                          setSelectedEmployee(employee.id);
                          setSelectedDate(''); // Reset date selection
                          setSelectedTime(''); // Reset time selection
                        }
                      }}
                    >
                      <div className="employee-info">
                        <h4>{employee.name}</h4>
                        <span className="specialty">{employee.specialty}</span>
                        <div className="rating">
                          ‚≠ê {employee.rating}
                        </div>
                        {employee.status !== 'active' && (
                          <span className="unavailable-label">No disponible</span>
                        )}
                      </div>
                    </div>
                  )) || []}
                </div>
              )}
            </div>
          )}

          {/* Selecci√≥n de fecha */}
          {selectedEmployee && (
            <div className="form-section">
              <label>Selecciona la fecha:</label>
              <div className="date-options">
                {getAvailableDates().map(date => (
                  <button
                    key={date}
                    className={`date-btn ${selectedDate === date ? 'selected' : ''}`}
                    onClick={() => {
                      setSelectedDate(date);
                      setSelectedTime(''); // Reset time selection
                    }}
                  >
                    {formatDate(date)}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Selecci√≥n de hora */}
          {selectedDate && (
            <div className="form-section">
              <label>Selecciona la hora:</label>
              <div className="time-options">
                {timeSlots.map(time => (
                  <button
                    key={time}
                    className={`time-btn ${selectedTime === time ? 'selected' : ''} ${!isTimeSlotAvailable(time) ? 'unavailable' : ''}`}
                    onClick={() => setSelectedTime(time)}
                    disabled={!isTimeSlotAvailable(time)}
                  >
                    {time}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Bot√≥n de confirmar */}
          {selectedService && selectedEmployee && selectedDate && selectedTime && (
            <div className="form-section">
              <button 
                className="confirm-btn" 
                onClick={handleBookAppointment}
                disabled={loading}
              >
                {loading ? 'Agendando...' : 'Confirmar Cita'}
              </button>
            </div>
          )}
        </div>

        {/* Lista de citas */}
        <div className="appointments-list">
          <h3>Mis Citas</h3>
          {loading && appointments.length === 0 ? (
            <div className="loading-appointments">
              <div className="spinner"></div>
              <p>Cargando citas...</p>
            </div>
          ) : appointments.length === 0 ? (
            <div className="no-appointments">
              <p>No tienes citas agendadas</p>
            </div>
          ) : (
            <div className="appointments">
              {appointments.map(appointment => (
                <div key={appointment._id || appointment.id} className="appointment-card">
                  <div className="appointment-header">
                    <span className="service-icon">
                      {services.find(s => s.id === appointment.serviceType)?.icon || 'üìÖ'}
                    </span>
                    <div className="appointment-info">
                      <h4>{appointment.serviceName}</h4>
                      <span className="employee-name">con {appointment.employee?.name || 'Profesional'}</span>
                    </div>
                    {(appointment.status === 'pending' || appointment.status === 'confirmed') && (
                      <button 
                        className="cancel-btn"
                        onClick={() => cancelAppointment(appointment._id || appointment.id)}
                        disabled={loading}
                      >
                        ‚úï
                      </button>
                    )}
                  </div>
                  
                  <div className="appointment-details">
                    <div className="detail-item">
                      <span className="detail-label">Fecha:</span>
                      <span className="detail-value">{formatDate(appointment.date)}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Hora:</span>
                      <span className="detail-value">{appointment.time}</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Duraci√≥n:</span>
                      <span className="detail-value">{appointment.serviceDuration} min</span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Estado:</span>
                      <span className={`status ${getStatusClass(appointment.status)}`}>
                        {getStatusDisplay(appointment.status)}
                      </span>
                    </div>
                    {appointment.employee?.phone && (
                      <div className="detail-item">
                        <span className="detail-label">Contacto:</span>
                        <span className="detail-value">{appointment.employee.phone}</span>
                      </div>
                    )}
                    {appointment.notes && (
                      <div className="detail-item">
                        <span className="detail-label">Notas:</span>
                        <span className="detail-value">{appointment.notes}</span>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AppointmentBooking;
