import React, { useState } from 'react';
import './mapa.css';

const MapSection = () => {
  const [selectedMachine, setSelectedMachine] = useState(null);

  const machines = [
    // Zona Cardio (izquierda)
    { id: 1, name: 'Cinta 1', type: 'cardio', x: 15, y: 20 },
    { id: 2, name: 'Cinta 2', type: 'cardio', x: 15, y: 35 },
    { id: 3, name: 'Cinta 3', type: 'cardio', x: 15, y: 50 },
    { id: 4, name: 'El√≠ptica 1', type: 'cardio', x: 25, y: 25 },
    { id: 5, name: 'El√≠ptica 2', type: 'cardio', x: 25, y: 45 },
    { id: 6, name: 'Bicicleta 1', type: 'cardio', x: 35, y: 20 },
    { id: 7, name: 'Bicicleta 2', type: 'cardio', x: 35, y: 35 },
    { id: 8, name: 'Escaladora', type: 'cardio', x: 35, y: 50 },

    // Zona de Fuerza (centro)
    { id: 9, name: 'Press Pecho', type: 'strength', x: 55, y: 15 },
    { id: 10, name: 'Press Piernas', type: 'strength', x: 65, y: 15 },
    { id: 11, name: 'Press Hombros', type: 'strength', x: 75, y: 15 },
    { id: 12, name: 'Polea Alta', type: 'strength', x: 55, y: 30 },
    { id: 13, name: 'Polea Baja', type: 'strength', x: 65, y: 30 },
    { id: 14, name: 'Remo', type: 'strength', x: 75, y: 30 },
    { id: 15, name: 'Banco Inclinado', type: 'strength', x: 55, y: 45 },
    { id: 16, name: 'Curl B√≠ceps', type: 'strength', x: 65, y: 45 },

    // Pesos Libres (derecha)
    { id: 17, name: 'Rack Sentadillas', type: 'weights', x: 85, y: 20 },
    { id: 18, name: 'Banco Plano', type: 'weights', x: 90, y: 35 },
    { id: 19, name: 'Mancuernas', type: 'weights', x: 85, y: 50 },
    { id: 20, name: 'Barras Ol√≠mpicas', type: 'weights', x: 95, y: 25 },

    // √Årea Funcional (abajo centro)
    { id: 21, name: 'TRX', type: 'functional', x: 45, y: 70 },
    { id: 22, name: 'Pelotas', type: 'functional', x: 55, y: 75 },
    { id: 23, name: 'Colchonetas', type: 'functional', x: 65, y: 70 },
    { id: 24, name: 'Cuerdas', type: 'functional', x: 75, y: 75 }
  ];

  const getMachineColor = (type) => {
    switch (type) {
      case 'cardio': return '#e74c3c';
      case 'strength': return '#9b59b6';
      case 'weights': return '#f39c12';
      case 'functional': return '#3498db';
      default: return '#95a5a6';
    }
  };

  const getMachineIcon = (type) => {
    switch (type) {
      case 'cardio': return 'üèÉ';
      case 'strength': return 'üèãÔ∏è';
      case 'weights': return 'üí™';
      case 'functional': return 'ü§∏';
      default: return '‚ö°';
    }
  };

  return (
    <div className="map-section">
      <div className="map-header">
        <h2>üó∫Ô∏è Mapa del Gimnasio</h2>
        <div className="legend">
          <div className="legend-item">
            <span className="legend-color cardio"></span>
            <span>Cardio</span>
          </div>
          <div className="legend-item">
            <span className="legend-color strength"></span>
            <span>Fuerza</span>
          </div>
          <div className="legend-item">
            <span className="legend-color weights"></span>
            <span>Pesos Libres</span>
          </div>
          <div className="legend-item">
            <span className="legend-color functional"></span>
            <span>Funcional</span>
          </div>
        </div>
      </div>

      <div className="gym-map">
        <div className="gym-floor">
          
          {/* √Åreas de fondo */}
          <div className="floor-area cardio-area">
            <span className="area-label">ZONA CARDIO</span>
          </div>
          <div className="floor-area strength-area">
            <span className="area-label">ZONA DE FUERZA</span>
          </div>
          <div className="floor-area weights-area">
            <span className="area-label">PESOS LIBRES</span>
          </div>
          <div className="floor-area functional-area">
            <span className="area-label">√ÅREA FUNCIONAL</span>
          </div>
          <div className="floor-area entrance-area">
            <span className="area-label">ENTRADA</span>
          </div>
          <div className="floor-area services-area">
            <span className="area-label">SERVICIOS</span>
          </div>

          {/* M√°quinas posicionadas */}
          {machines.map((machine) => (
            <div
              key={machine.id}
              className="machine-marker"
              style={{
                left: `${machine.x}%`,
                top: `${machine.y}%`,
                backgroundColor: getMachineColor(machine.type)
              }}
              onClick={() => setSelectedMachine(machine)}
              title={machine.name}
            >
              <span className="machine-icon">{getMachineIcon(machine.type)}</span>
              <span className="machine-label">{machine.name}</span>
            </div>
          ))}

          {/* Elementos estructurales */}
          <div className="gym-element reception">
            <span className="element-label">Recepci√≥n</span>
          </div>
          <div className="gym-element lockers">
            <span className="element-label">Vestidores</span>
          </div>
          <div className="gym-element bathrooms">
            <span className="element-label">Ba√±os</span>
          </div>
          <div className="gym-element emergency-exit">
            <span className="element-label">Salida Emergencia</span>
          </div>

        </div>
      </div>

      {selectedMachine && (
        <div className="machine-info">
          <h3>{selectedMachine.name}</h3>
          <div className="machine-details">
            <span className="machine-type">
              Tipo: {selectedMachine.type === 'cardio' ? 'Cardio' : 
                     selectedMachine.type === 'strength' ? 'Fuerza' :
                     selectedMachine.type === 'weights' ? 'Pesos Libres' : 'Funcional'}
            </span>
            <p className="machine-description">
              Equipo disponible para entrenamiento. Consulta con el personal para instrucciones espec√≠ficas.
            </p>
          </div>
          <button 
            className="info-btn"
            onClick={() => setSelectedMachine(null)}
          >
            Cerrar
          </button>
        </div>
      )}
    </div>
  );
};

export default MapSection;
