import React from 'react';

const MachineDetail = ({ machine, onClose }) => {
  if (!machine) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content large-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>{machine.name}</h3>
          <button className="close-btn" onClick={onClose}>✕</button>
        </div>
        
        <div className="modal-body">
          <div className="machine-details">
            {/* Imagen de la máquina */}
            <div className="detail-section">
              <img
                src={machine.image}
                alt={machine.name}
                className="machine-detail-image"
                style={{ 
                  maxWidth: '100%', 
                  height: 'auto', 
                  borderRadius: '8px',
                  maxHeight: '300px',
                  objectFit: 'cover'
                }}
                onError={(e) => {
                  e.target.src = '/placeholder-machine.jpg';
                }}
              />
            </div>
            
            {/* Solo información de la base de datos */}
            <div className="detail-section">
              <h4>Información</h4>
              <p><strong>ID:</strong> {machine.id}</p>
              <p><strong>Categoría:</strong> {machine.categoria}</p>
              <p><strong>Cantidad:</strong> {machine.cantidad}</p>
              <p><strong>Pesos:</strong> {machine.pesos}</p>
              <p><strong>Estado:</strong> {machine.status === 'available' ? 'Disponible' : 'No disponible'}</p>
              
              {/* Video si existe */}
              {machine.video && (
                <div className="video-section" style={{ marginTop: '20px' }}>
                  <h4>Video Tutorial</h4>
                  <video
                    controls
                    width="100%"
                    style={{ maxWidth: '400px', borderRadius: '8px' }}
                  >
                    <source src={machine.video} type="video/mp4" />
                    Tu navegador no soporta el elemento video.
                  </video>
                </div>
              )}
            </div>
          </div>
        </div>
        
        <div className="modal-footer">
          <button className="btn-secondary" onClick={onClose}>Cerrar</button>
        </div>
      </div>
    </div>
  );
};

export default MachineDetail;
