import React, { useState } from 'react';
import './mapa.css';

const MapSection = () => {
  const [selectedMachine, setSelectedMachine] = useState(null);

  const gymLayout = {
    entrance: {
      name: "Entrada",
      items: ["Recepci√≥n", "Vestidores", "Casilleros"]
    },
    cardio: {
      name: "Zona Cardio",
      items: [
        "Cinta Caminadora 1", "Cinta Caminadora 2", "Cinta Caminadora 3",
        "El√≠ptica 1", "El√≠ptica 2", "Bicicleta Est√°tica 1", 
        "Bicicleta Est√°tica 2", "Escaladora"
      ]
    },
    strength: {
      name: "Zona de Fuerza",
      items: [
        "Press de Pecho", "Press de Piernas", "Press de Hombros",
        "Polea Alta", "Polea Baja", "M√°quina de Remo",
        "Banco Inclinado", "M√°quina de Curl"
      ]
    },
    freeWeights: {
      name: "Pesos Libres",
      items: [
        "Mancuernas 5-50kg", "Barras Ol√≠mpicas", "Rack de Sentadillas",
        "Banco Plano", "Banco Inclinado", "Discos Ol√≠mpicos",
        "Kettlebells", "Barras Z"
      ]
    },
    functional: {
      name: "√Årea Funcional",
      items: [
        "Zona de Estiramiento", "Pelotas de Ejercicio", "TRX",
        "Colchonetas", "Cuerdas de Batalla", "Cajones Pliom√©tricos",
        "Bandas El√°sticas", "Barras de Paralelas"
      ]
    },
    services: {
      name: "Servicios",
      items: ["Ba√±os", "Duchas", "Sauna", "√Årea de Descanso", "Bebederos"]
    }
  };

  return (
    <div className="map-section">
      <div className="map-header">
        <h2>üó∫Ô∏è Mapa del Gimnasio</h2>
        <div className="map-info">
          <span>Plano General - CloudFit</span>
        </div>
      </div>

      <div className="gym-map">
        <div className="gym-floor">
          
          {/* Entrada */}
          <div className="gym-zone entrance-zone">
            <div className="zone-header">
              <h3>{gymLayout.entrance.name}</h3>
            </div>
            <div className="zone-content">
              {gymLayout.entrance.items.map((item, index) => (
                <div key={index} className="facility-item entrance-item">
                  <span className="facility-icon">üö™</span>
                  <span className="facility-name">{item}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Zona Cardio */}
          <div className="gym-zone cardio-zone">
            <div className="zone-header">
              <h3>{gymLayout.cardio.name}</h3>
            </div>
            <div className="zone-content">
              {gymLayout.cardio.items.map((item, index) => (
                <div 
                  key={index} 
                  className="machine-item cardio-machine"
                  onClick={() => setSelectedMachine({name: item, zone: 'Cardio'})}
                >
                  <span className="machine-icon">üèÉ</span>
                  <span className="machine-name">{item}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Zona de Fuerza */}
          <div className="gym-zone strength-zone">
            <div className="zone-header">
              <h3>{gymLayout.strength.name}</h3>
            </div>
            <div className="zone-content">
              {gymLayout.strength.items.map((item, index) => (
                <div 
                  key={index} 
                  className="machine-item strength-machine"
                  onClick={() => setSelectedMachine({name: item, zone: 'Fuerza'})}
                >
                  <span className="machine-icon">üèãÔ∏è</span>
                  <span className="machine-name">{item}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Pesos Libres */}
          <div className="gym-zone free-weights-zone">
            <div className="zone-header">
              <h3>{gymLayout.freeWeights.name}</h3>
            </div>
            <div className="zone-content">
              {gymLayout.freeWeights.items.map((item, index) => (
                <div 
                  key={index} 
                  className="machine-item weights-machine"
                  onClick={() => setSelectedMachine({name: item, zone: 'Pesos Libres'})}
                >
                  <span className="machine-icon">üí™</span>
                  <span className="machine-name">{item}</span>
                </div>
              ))}
            </div>
          </div>

          {/* √Årea Funcional */}
          <div className="gym-zone functional-zone">
            <div className="zone-header">
              <h3>{gymLayout.functional.name}</h3>
            </div>
            <div className="zone-content">
              {gymLayout.functional.items.map((item, index) => (
                <div 
                  key={index} 
                  className="machine-item functional-machine"
                  onClick={() => setSelectedMachine({name: item, zone: 'Funcional'})}
                >
                  <span className="machine-icon">ü§∏</span>
                  <span className="machine-name">{item}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Servicios */}
          <div className="gym-zone services-zone">
            <div className="zone-header">
              <h3>{gymLayout.services.name}</h3>
            </div>
            <div className="zone-content">
              {gymLayout.services.items.map((item, index) => (
                <div key={index} className="facility-item service-item">
                  <span className="facility-icon">üöø</span>
                  <span className="facility-name">{item}</span>
                </div>
              ))}
            </div>
          </div>

        </div>
      </div>

      {selectedMachine && (
        <div className="machine-info">
          <h3>{selectedMachine.name}</h3>
          <div className="machine-details">
            <span className="machine-zone">Zona: {selectedMachine.zone}</span>
            <p className="machine-description">
              Equipo disponible para entrenamiento. Consulta con el personal para instrucciones de uso.
            </p>
          </div>
          <button 
            className="info-btn"
            onClick={() => setSelectedMachine(null)}
          >
            Cerrar Informaci√≥n
          </button>
        </div>
      )}
    </div>
  );
};

export default MapSection;
