import React, { useState, useEffect } from 'react';

const UsersManagement = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage] = useState(10);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showUserModal, setShowUserModal] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, []);

  useEffect(() => {
    filterUsers();
  }, [searchTerm, users]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      // Aquí harías la llamada a tu API
      // const response = await fetch('/api/admin/users');
      // const usersData = await response.json();
      
      // Datos de ejemplo
      const mockUsers = [
        {
          id: '501234',
          username: 'juan_perez',
          email: 'juan.perez@gmail.com',
          planType: 'annual',
          subscriptionStatus: 'active',
          registrationDate: '2025-06-23',
          phone: '1234567890',
          lastLogin: '2025-06-23'
        },
        {
          id: '501235',
          username: 'maria_garcia',
          email: 'maria.garcia@gmail.com',
          planType: 'monthly',
          subscriptionStatus: 'active',
          registrationDate: '2025-06-22',
          phone: '0987654321',
          lastLogin: '2025-06-22'
        },
        {
          id: '501236',
          username: 'carlos_lopez',
          email: 'carlos.lopez@gmail.com',
          planType: 'monthly',
          subscriptionStatus: 'expired',
          registrationDate: '2025-05-15',
          phone: '1122334455',
          lastLogin: '2025-06-20'
        }
      ];
      
      setUsers(mockUsers);
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const filterUsers = () => {
    if (!searchTerm) {
      setFilteredUsers(users);
    } else {
      const filtered = users.filter(user =>
        user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.id.includes(searchTerm)
      );
      setFilteredUsers(filtered);
    }
    setCurrentPage(1);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleUserAction = async (userId, action) => {
    try {
      // Aquí harías la llamada a tu API
      // await fetch(`/api/admin/users/${userId}/${action}`, { method: 'POST' });
      
      console.log(`Acción ${action} ejecutada en usuario ${userId}`);
      
      // Actualizar el estado local
      setUsers(prevUsers =>
        prevUsers.map(user =>
          user.id === userId
            ? { ...user, subscriptionStatus: action === 'suspend' ? 'suspended' : 'active' }
            : user
        )
      );
    } catch (error) {
      console.error(`Error executing ${action}:`, error);
    }
  };

  const openUserModal = (user) => {
    setSelectedUser(user);
    setShowUserModal(true);
  };

  const closeUserModal = () => {
    setSelectedUser(null);
    setShowUserModal(false);
  };

  // Paginación
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="users-management">
      <div className="section-header">
        <h2>Gestión de Usuarios</h2>
        <div className="users-stats">
          <span className="stat-item">
            <strong>{users.length}</strong> usuarios totales
          </span>
          <span className="stat-item">
            <strong>{users.filter(u => u.subscriptionStatus === 'active').length}</strong> activos
          </span>
        </div>
      </div>

      <div className="users-controls">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Buscar por nombre, email o ID..."
            value={searchTerm}
            onChange={handleSearchChange}
          />
          <button className="search-btn">🔍</button>
        </div>
        
        <div className="filter-buttons">
          <button className="filter-btn active">Todos</button>
          <button className="filter-btn">Activos</button>
          <button className="filter-btn">Suspendidos</button>
          <button className="filter-btn">Expirados</button>
        </div>
      </div>

      {loading ? (
        <div className="loading">
          <div className="spinner"></div>
          <p>Cargando usuarios...</p>
        </div>
      ) : (
        <>
          <div className="users-table">
            <table>
              <thead>
                <tr>
                  <th>ID Usuario</th>
                  <th>Nombre de Usuario</th>
                  <th>Email</th>
                  <th>Plan</th>
                  <th>Estado</th>
                  <th>Registro</th>
                  <th>Último Login</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {currentUsers.map(user => (
                  <tr key={user.id}>
                    <td className="user-id">{user.id}</td>
                    <td className="username">
                      <div className="user-info">
                        <div className="user-avatar">👤</div>
                        <span>{user.username}</span>
                      </div>
                    </td>
                    <td>{user.email}</td>
                    <td>
                      <span className={`plan-badge ${user.planType}`}>
                        {user.planType === 'annual' ? 'Anual' : 'Mensual'}
                      </span>
                    </td>
                    <td>
                      <span className={`status-badge ${user.subscriptionStatus}`}>
                        {user.subscriptionStatus === 'active' && 'Activo'}
                        {user.subscriptionStatus === 'expired' && 'Expirado'}
                        {user.subscriptionStatus === 'suspended' && 'Suspendido'}
                      </span>
                    </td>
                    <td>{new Date(user.registrationDate).toLocaleDateString()}</td>
                    <td>{new Date(user.lastLogin).toLocaleDateString()}</td>
                    <td>
                      <div className="action-buttons">
                        <button
                          className="btn-view"
                          onClick={() => openUserModal(user)}
                          title="Ver detalles"
                        >
                          👁️
                        </button>
                        <button
                          className="btn-edit"
                          onClick={() => console.log('Editar', user.id)}
                          title="Editar"
                        >
                          ✏️
                        </button>
                        {user.subscriptionStatus === 'active' ? (
                          <button
                            className="btn-suspend"
                            onClick={() => handleUserAction(user.id, 'suspend')}
                            title="Suspender"
                          >
                            ⏸️
                          </button>
                        ) : (
                          <button
                            className="btn-activate"
                            onClick={() => handleUserAction(user.id, 'activate')}
                            title="Activar"
                          >
                            ▶️
                          </button>
                        )}
                        <button
                          className="btn-delete"
                          onClick={() => handleUserAction(user.id, 'delete')}
                          title="Eliminar"
                        >
                          🗑️
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Paginación */}
          <div className="pagination">
            <button
              className="page-btn"
              onClick={() => paginate(currentPage - 1)}
              disabled={currentPage === 1}
            >
              ← Anterior
            </button>
            
            <div className="page-numbers">
              {Array.from({ length: totalPages }, (_, i) => (
                <button
                  key={i + 1}
                  className={`page-number ${currentPage === i + 1 ? 'active' : ''}`}
                  onClick={() => paginate(i + 1)}
                >
                  {i + 1}
                </button>
              ))}
            </div>
            
            <button
              className="page-btn"
              onClick={() => paginate(currentPage + 1)}
              disabled={currentPage === totalPages}
            >
              Siguiente →
            </button>
          </div>

          <div className="pagination-info">
            Mostrando {indexOfFirstUser + 1} a {Math.min(indexOfLastUser, filteredUsers.length)} de {filteredUsers.length} usuarios
          </div>
        </>
      )}

      {/* Modal de detalles del usuario */}
      {showUserModal && selectedUser && (
        <div className="modal-overlay" onClick={closeUserModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Detalles del Usuario</h3>
              <button className="close-btn" onClick={closeUserModal}>✕</button>
            </div>
            
            <div className="modal-body">
              <div className="user-details">
                <div className="detail-section">
                  <h4>Información Personal</h4>
                  <p><strong>ID:</strong> {selectedUser.id}</p>
                  <p><strong>Usuario:</strong> {selectedUser.username}</p>
                  <p><strong>Email:</strong> {selectedUser.email}</p>
                  <p><strong>Teléfono:</strong> {selectedUser.phone}</p>
                </div>
                
                <div className="detail-section">
                  <h4>Suscripción</h4>
                  <p><strong>Plan:</strong> {selectedUser.planType === 'annual' ? 'Anual' : 'Mensual'}</p>
                  <p><strong>Estado:</strong> {selectedUser.subscriptionStatus}</p>
                  <p><strong>Registro:</strong> {new Date(selectedUser.registrationDate).toLocaleDateString()}</p>
                  <p><strong>Último Login:</strong> {new Date(selectedUser.lastLogin).toLocaleDateString()}</p>
                </div>
              </div>
            </div>
            
            <div className="modal-footer">
              <button className="btn-secondary" onClick={closeUserModal}>Cerrar</button>
              <button className="btn-primary">Editar Usuario</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UsersManagement;
