import React, { useState, useEffect } from 'react';
import './Calendario.css';

const Schedule = () => {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [error, setError] = useState('');

  useEffect(() => {
    fetchAppointments();
  }, [selectedDate]);

  const fetchAppointments = async () => {
    setLoading(true);
    setError('');
    
    try {
      const token = localStorage.getItem('token');
      const dateStr = selectedDate.toISOString().split('T')[0]; // YYYY-MM-DD
      
      const response = await fetch(`http://localhost:5000/api/empleados/appointments?date=${dateStr}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      const data = await response.json();
      
      if (response.ok && data.success) {
        setAppointments(data.appointments || []);
      } else {
        // Datos de ejemplo mientras implementas el backend
        setAppointments([
          {
            id: 1,
            time: '09:00',
            client: 'Mar√≠a Gonz√°lez',
            service: 'Corte y Peinado',
            duration: 60,
            status: 'confirmed'
          },
          {
            id: 2,
            time: '10:30',
            client: 'Carlos Rodr√≠guez',
            service: 'Corte de Cabello',
            duration: 45,
            status: 'confirmed'
          },
          {
            id: 3,
            time: '14:00',
            client: 'Ana Mart√≠nez',
            service: 'Entrenamiento Personal',
            duration: 90,
            status: 'pending'
          },
          {
            id: 4,
            time: '16:30',
            client: 'Luis Fernando',
            service: 'Corte y Barba',
            duration: 75,
            status: 'confirmed'
          }
        ]);
      }
    } catch (error) {
      console.error('Error fetching appointments:', error);
      setError('Error al cargar las citas');
      // Datos de ejemplo en caso de error
      setAppointments([]);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (date) => {
    const options = { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    };
    return date.toLocaleDateString('es-ES', options);
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'confirmed':
        return '#7dd3c0';
      case 'pending':
        return '#f39c12';
      case 'cancelled':
        return '#e74c3c';
      default:
        return '#95a5a6';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'confirmed':
        return 'Confirmada';
      case 'pending':
        return 'Pendiente';
      case 'cancelled':
        return 'Cancelada';
      default:
        return 'Sin estado';
    }
  };

  const getServiceIcon = (service) => {
    if (service.toLowerCase().includes('corte')) return '‚úÇÔ∏è';
    if (service.toLowerCase().includes('peinado')) return 'üíá‚Äç‚ôÄÔ∏è';
    if (service.toLowerCase().includes('entrenamiento') || service.toLowerCase().includes('coach')) return 'üèãÔ∏è‚Äç‚ôÇÔ∏è';
    if (service.toLowerCase().includes('barba')) return 'ü™í';
    return 'üìÖ';
  };

  const calculateEndTime = (startTime, duration) => {
    const [hours, minutes] = startTime.split(':').map(Number);
    const startMinutes = hours * 60 + minutes;
    const endMinutes = startMinutes + duration;
    const endHours = Math.floor(endMinutes / 60);
    const endMins = endMinutes % 60;
    return `${endHours.toString().padStart(2, '0')}:${endMins.toString().padStart(2, '0')}`;
  };

  const changeDate = (days) => {
    const newDate = new Date(selectedDate);
    newDate.setDate(newDate.getDate() + days);
    setSelectedDate(newDate);
  };

  const isToday = (date) => {
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  if (loading) {
    return (
      <div className="schedule-container">
        <div className="loading-schedule">
          <div className="loading-spinner"></div>
          <p>Cargando horario...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="schedule-container">
      <div className="schedule-header">
        <h2 className="schedule-title">Mi Horario</h2>
        
        <div className="date-navigator">
          <button className="date-btn" onClick={() => changeDate(-1)}>
            <span>‚Üê</span>
          </button>
          
          <div className="current-date">
            <div className="date-text">{formatDate(selectedDate)}</div>
            {isToday(selectedDate) && <div className="today-badge">Hoy</div>}
          </div>
          
          <button className="date-btn" onClick={() => changeDate(1)}>
            <span>‚Üí</span>
          </button>
        </div>
      </div>

      {error && (
        <div className="error-message">
          <span className="error-icon">‚ö†Ô∏è</span>
          <span>{error}</span>
        </div>
      )}

      <div className="appointments-section">
        <div className="appointments-header">
          <h3>Citas del D√≠a</h3>
          <div className="appointments-count">
            <span className="count-number">{appointments.length}</span>
            <span className="count-text">citas</span>
          </div>
        </div>

        {appointments.length === 0 ? (
          <div className="no-appointments">
            <div className="no-appointments-icon">üìÖ</div>
            <h4>Sin citas programadas</h4>
            <p>¬°Disfruta tu d√≠a libre!</p>
          </div>
        ) : (
          <div className="appointments-list">
            {appointments.map((appointment) => (
              <div key={appointment.id} className="appointment-card">
                <div className="appointment-time">
                  <div className="start-time">{appointment.time}</div>
                  <div className="time-separator">-</div>
                  <div className="end-time">
                    {calculateEndTime(appointment.time, appointment.duration)}
                  </div>
                </div>
                
                <div className="appointment-details">
                  <div className="client-info">
                    <span className="client-icon">üë§</span>
                    <span className="client-name">{appointment.client}</span>
                  </div>
                  
                  <div className="service-info">
                    <span className="service-icon">{getServiceIcon(appointment.service)}</span>
                    <span className="service-name">{appointment.service}</span>
                  </div>
                  
                  <div className="duration-info">
                    <span className="duration-icon">‚è±Ô∏è</span>
                    <span className="duration-text">{appointment.duration} min</span>
                  </div>
                </div>
                
                <div className="appointment-status">
                  <div 
                    className="status-indicator"
                    style={{ backgroundColor: getStatusColor(appointment.status) }}
                  ></div>
                  <span className="status-text">
                    {getStatusText(appointment.status)}
                  </span>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="schedule-summary">
        <div className="summary-card">
          <div className="summary-icon">‚è∞</div>
          <div className="summary-text">
            <span className="summary-title">Tiempo Total</span>
            <span className="summary-value">
              {appointments.reduce((total, apt) => total + apt.duration, 0)} min
            </span>
          </div>
        </div>
        
        <div className="summary-card">
          <div className="summary-icon">‚úÖ</div>
          <div className="summary-text">
            <span className="summary-title">Confirmadas</span>
            <span className="summary-value">
              {appointments.filter(apt => apt.status === 'confirmed').length}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Schedule;
